import React, { useState, useEffect } from 'react';
import './styles.css';

const Holon = ({ node, onSelect, onAdd, onRemove }) => {
  return (
    <div className={`holon holon-level-${node.level}`} onClick={() => onSelect(node)}>
      <div className="holon-details">
        <div className="name">{node.name}</div>
        <div className="buttons">
          <button className="add-button" onClick={(e) => { e.stopPropagation(); onAdd(node); }}>+</button>
          <button className="remove-button" onClick={(e) => { e.stopPropagation(); onRemove(node.id); }}>x</button>
        </div>
      </div>
    </div>
  );
};

const flattenNodes = (nodes, level = 0) => {
  return nodes.reduce((acc, node) => {
    const newNode = { ...node, level };
    acc.push(newNode);
    if (node.children) {
      acc = acc.concat(flattenNodes(node.children, level + 1));
    }
    return acc;
  }, []);
};

const App = () => {
  const [nodes, setNodes] = useState([
    // Initial nodes structure...
  ]);
  const [selectedHolon, setSelectedHolon] = useState(null);
  const [idCounter, setIdCounter] = useState(3); // Adjusted to account for the initial nodes
  const [flattenedNodes, setFlattenedNodes] = useState([]);

  useEffect(() => {
    setFlattenedNodes(flattenNodes(nodes));
  }, [nodes]);

  const selectHolon = (node) => {
    setSelectedHolon(node);
  };

  const addChild = (node) => {
    const childName = prompt('Enter the name of the child:');
    if (!childName || childName.trim() === '') {
      alert('Please enter a valid name for the child.');
      return;
    }

    const newId = `node-${idCounter}`;
    const newNode = {
      id: newId,
      name: childName,
      description: '',
      children: []
    };

    setIdCounter(idCounter + 1);

    // Similar to the addNodeRecursively function described earlier
    // Update the nodes state with the new child
  };

  const removeChild = (nodeId) => {
    // Similar to the removeNodeRecursively function described earlier
    // Update the nodes state by removing the specified node
  };

  return (
    <div className="app-container">
      {flattenedNodes.map(node => (
        <Holon key={node.id} node={node} onSelect={selectHolon} onAdd={addChild} onRemove={removeChild} />
      ))}
      <div className="holon-details">
        {selectedHolon ? (
          <>
            <h2>Holon Details</h2>
            <p>Name: {selectedHolon.name}</p>
            <p>Description: {selectedHolon.description || "No description provided."}</p>
          </>
        ) : (
          <p>Select a holon to see details.</p>
        )}
      </div>
    </div>
  );
};

export default App;
