import React, { useState } from 'react';
import './styles.css';

// Represents a single node in the tree structure
const Holon = ({ node, onSelect, onAdd, onRemove }) => {
  const { id, name, children } = node;

  return (
    <div className="holon">
      <div className="holon-details" onClick={() => onSelect(node)}>
        <div className="name">{name}</div>
        <div className="buttons">
          <button className="add-button" onClick={(e) => { e.stopPropagation(); onAdd(id); }}>+</button>
          <button className="remove-button" onClick={(e) => { e.stopPropagation(); onRemove(id); }}>x</button>
        </div>
      </div>
      {children.length > 0 && (
        <div className="children">
          {children.map(child => (
            <Holon key={child.id} node={child} onSelect={onSelect} onAdd={onAdd} onRemove={onRemove} />
          ))}
        </div>
      )}
    </div>
  );
};

const App = () => {
  const [nodes, setNodes] = useState([
    // Initial node structure
  ]);
  const [selectedHolon, setSelectedHolon] = useState(null);
  const [idCounter, setIdCounter] = useState(3); 

  // Selects a holon to display its details
  const selectHolon = (node) => {
    setSelectedHolon(node);
  };

  // Adds a child node to the specified parent node
  const addChild = (parentId) => {
    const childName = prompt('Enter the name of the child:');
    if (!childName) return; // Exit if no name is provided

    const newNode = {
      id: `node-${idCounter}`,
      name: childName,
      description: '',
      children: []
    };

    const addNodeRecursively = (nodes, parentId, newNode) => {
      return nodes.map(node => {
        if (node.id === parentId) {
          return { ...node, children: [...node.children, newNode] };
        } else if (node.children.length) {
          return { ...node, children: addNodeRecursively(node.children, parentId, newNode) };
        }
        return node;
      });
    };

    setNodes(prevNodes => addNodeRecursively(prevNodes, parentId, newNode));
    setIdCounter(idCounter + 1); // Increment the ID counter for the next node
  };

  // Removes a node by its ID
  const removeChild = (nodeId) => {
    const removeNodeRecursively = (nodes, nodeId) => {
      return nodes.reduce((acc, node) => {
        if (node.id !== nodeId) {
          return [...acc, { ...node, children: removeNodeRecursively(node.children, nodeId) }];
        }
        return acc;
      }, []);
    };

    setNodes(prevNodes => removeNodeRecursively(prevNodes, nodeId));
  };

  return (
    <div className="app-container">
      <div className="holons-container">
        <Holon node={nodes[0]} onSelect={selectHolon} onAdd={addChild} onRemove={removeChild} />
      </div>
      <div className="holon-details">
        {selectedHolon ? (
          <>
            <h2>Holon Details</h2>
            <p>Name: {selectedHolon.name}</p>
            <p>Description: {selectedHolon.description}</p>
          </>
        ) : <p>Select a holon to see details.</p>}
      </div>
    </div>
  );
};

export default App;
