import React, { useState } from 'react';
import { Tree, TreeNode } from 'react-organizational-chart';
import './styles.css';

const Holon = ({ node, onAddChild, onRemoveNode, onSelectNode, isSelected, addNewRoot }) => {
  const handleActionClick = (e, action) => {
    e.stopPropagation();
    action();
  };

  const selected = isSelected(node);

  return (
    <TreeNode label={
      <div className={`tree-label ${selected ? 'selected' : ''}`} onClick={() => onSelectNode(node)}>
        <div className="name">{node.name}</div>
        <div className="buttons">
          <button className="add-button" onClick={(e) => handleActionClick(e, () => onAddChild(node, true))}>+</button>

          {node.id !== 'root' && (
            <button className="remove-button" onClick={(e) => handleActionClick(e, () => onRemoveNode(node.id))}>x</button>
          )}
        </div>
      </div>
    }>
      {node.children && node.children.map(child => (
        <Holon 
          key={child.id} 
          node={child} 
          onAddChild={onAddChild} 
          onRemoveNode={onRemoveNode} 
          onSelectNode={onSelectNode} 
          isSelected={isSelected}
          addNewRoot={addNewRoot}
        />
      ))}
    </TreeNode>
  );
};

const App = () => {
  const initialNodes = {
    id: 'root',
    name: 'Information System',
    description: 'An information system is an organized combination of people, hardware, software, communication networks, and data resources that collects, transforms, and disseminates information in an organization.',
    children: []
  };

  const [nodes, setNodes] = useState(initialNodes);
  const [selectedHolon, setSelectedHolon] = useState(null);

  const onAddChild = (parentNode, isRoot = false) => {
    const relationshipType = prompt('Enter "child" to add a child node or "parent" to add this as a child to a new root:');
    const nodeName = prompt('Enter the name of the node:');

    if (!nodeName) return;

    if (relationshipType.toLowerCase() === "child") {
      const newChild = {
        id: `${parentNode.id}-${Math.random()}`,
        name: nodeName,
        children: []
      };

      const addNode = (nodes, parentId) => {
        if (nodes.id === parentId) {
          nodes.children.push(newChild);
          return;
        }

        nodes.children.forEach(child => addNode(child, parentId));
      };

      const newNodes = { ...nodes };
      addNode(newNodes, parentNode.id);
      setNodes(newNodes);
    } else if (relationshipType.toLowerCase() === "parent") {
      const newParent = {
        id: `root-${Math.random()}`,
        name: nodeName,
        children: [parentNode]
      };

      setNodes(newParent);
    }
  };

  const onRemoveNode = (nodeId) => {
    const removeNode = (nodes, nodeIdToRemove) => {
      if (nodes.id === nodeIdToRemove) {
        return null; // Can't remove root
      }

      nodes.children = nodes.children.filter(child => child.id !== nodeIdToRemove);
      nodes.children.forEach(child => removeNode(child, nodeIdToRemove));

      return nodes;
    };

    const newNodes = removeNode({ ...nodes }, nodeId);
    setNodes(newNodes);
    setSelectedHolon(null);
  };

  const addNewRoot = () => {
    const rootName = prompt('Enter the name for the new root:');
    if (!rootName) return;

    const newRoot = {
      id: `root-${Math.random()}`,
      name: rootName,
      children: [nodes]
    };

    setNodes(newRoot);
    setSelectedHolon(null);
  };

  const isSelected = (node) => selectedHolon && node.id === selectedHolon.id;

  return (
    <div className="app-container">
      <div className="steps-bar">
        <div className="step active" onClick={() => console.log("Go to Step 1")}>
          <div className="step-circle">1</div>
          <div className="step-label">Structure</div>
        </div>
        <div className="step" onClick={() => console.log("Go to Step 2")}>
          <div className="step-circle">2</div>
          <div className="step-label">Communication</div>
        </div>
      </div>
      <div className="content-area">
        <Tree lineWidth={"2px"} lineColor={"blue"} lineBorderRadius={"5px"}>
          <Holon
            node={nodes}
            onAddChild={onAddChild}
            onRemoveNode={onRemoveNode}
            onSelectNode={setSelectedHolon}
            isSelected={isSelected}
            addNewRoot={addNewRoot}
          />
        </Tree>
        <div className="holon-details">
          <h2>Holon Details</h2>
          {selectedHolon ? (
            <>
              <p className='holon-details-name'>Name: {selectedHolon.name}</p>
              <p className='holon-details-description'>Description: {selectedHolon.description || "No description available."}</p>
            </>
          ) : (
            <p className='no-selection'>No Selection</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default App;
