import React, { useState } from 'react';
import './styles.css';

const Holon = ({ name, onSelect, onAdd, onRemove }) => {
  return (
    <div className="holon" onClick={() => onSelect(name)}>
      <div className="name">{name}</div>
      <button className="add-button" onClick={(e) => { e.stopPropagation(); onAdd(name); }}>+</button>
      <button className="remove-button" onClick={(e) => { e.stopPropagation(); onRemove(name); }}>x</button>
    </div>
  );
};

const App = () => {
  const [nodes, setNodes] = useState([
    {
      id: 'root',
      name: 'Information System',
      description: 'An Information System is a set of components for collecting, storing, and processing data.',
      children: [
        {
          id: 'stakeholder',
          name: 'Stakeholder',
          description: 'A stakeholder refers to any individual, group, or organization that has an interest or concern in the system, its development, implementation, or outcomes. These stakeholders can be internal or external to the organization implementing the information system. Understanding and managing stakeholders is crucial for the success of an information system project.',
          children: [],
        } 
      ], 
    }
  ]);
  const [selectedHolon, setSelectedHolon] = useState(null);
  const [idCounter, setIdCounter] = useState(1);

  const selectHolon = (selectedName) => {
    const searchNode = (nodes, name) => {
      for (let node of nodes) {
        if (node.name === name) {
          return node;
        }
        if (node.children.length > 0) {
          const found = searchNode(node.children, name);
          if (found) return found;
        }
      }
      return null;
    };

    const holon = searchNode(nodes, selectedName);
    if (holon) {
      setSelectedHolon(holon);
    }
  };

  const addChild = (parentName) => {
    const childName = prompt('Enter the name of the child:');
    if (!childName || childName.trim() === '') {
      alert('Please enter a valid name for the child.');
      return;
    }

    setNodes((prevNodes) => {
      const newId = `node-${idCounter}`;
      setIdCounter(idCounter + 1);

      const addNodeRecursively = (nodes, parentName, childName, newId) => {
        return nodes.map(node => {
          if (node.name === parentName) {
            const newNode = {
              id: newId,
              name: childName,
              description: '',
              children: []
            };
            return { ...node, children: [...node.children, newNode] };
          } else if (node.children.length) {
            return { ...node, children: addNodeRecursively(node.children, parentName, childName, newId) };
          }
          return node;
        });
      };

      return addNodeRecursively(prevNodes, parentName, childName, newId);
    });
  };

  const removeChild = (nodeName) => {
    setNodes(prevNodes => removeNodeRecursively(prevNodes, nodeName));
  };

  const removeNodeRecursively = (nodes, nodeName) => {
    return nodes.reduce((acc, node) => {
      if (node.name !== nodeName) {
        const filteredChildren = removeNodeRecursively(node.children, nodeName);
        acc.push({ ...node, children: filteredChildren });
        return acc;
      }
      return acc;
    }, []);
  };

  const renderNodes = (nodes) => nodes.map(node => (
    <div key={node.id}>
      <Holon
        name={node.name}
        onSelect={selectHolon}
        onAdd={addChild}
        onRemove={removeChild}
      />
      {node.children.length > 0 && (
        <div className="children">
          {renderNodes(node.children)}
        </div>
      )}
    </div>
  ));

  return (
    <div className="app-container">
      <div className="holons-container">
        {renderNodes(nodes)}
      </div>
      <div className="holon-details">
        {selectedHolon ? (
          <>
            <h2>Holon Details</h2>
            <p>Name: {selectedHolon.name}</p>
            <p>Description: {selectedHolon.description || "No description provided."}</p>
          </>
        ) : (
          <p>Select a holon to see details.</p>
        )}
      </div>
    </div>
  );
};

export default App;
