import React, { useCallback, useEffect, useState } from "react";
import ReactFlow, {
  addEdge,
  useNodesState,
  useEdgesState,
  MarkerType,
} from "reactflow";
import Modal from "react-modal";
import "reactflow/dist/style.css";
import "bootstrap/dist/css/bootstrap.min.css";
import "./styles.css";
import CustomNodeFlow from "./CustomNodeFlow";

const nodeTypes = {
  customNode: CustomNodeFlow,
};

const fitViewOptions = { padding: 0.5 };

Modal.setAppElement("#root");

function CommunicationReactFlow(props) {
  const [nodes, setNodes, onNodesChange] = useNodesState(props.nodes);
  const [edges, setEdges, onEdgesChange] = useEdgesState(props.edges);
  const [sourceNode, setSourceNode] = useState(null);
  const [destinationNode, setDestinationNode] = useState();
  const [selectedNode, setSelectedNode] = useState(null);
  const [selectedInformationFlow, setSelectedInformationFlow] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [enableInformationFlow, setEnableInformationFlow] = useState(false);
  const [newNodeLabel, setNewNodeLabel] = useState("");
  const [disableAdd, setDisableAdd] = useState(false);
  const [resetFlow, setResetFlow] = useState(false);

  const [informationFlows, setInformationFlows] = useState([]);
  const onConnect = useCallback(
    (params) => setEdges((eds) => addEdge(params, eds)),
    [setEdges]
  );

  // useEffect(() => {
  //   console.log(sourceNode);
  //   informationFlows.push([sourceNode, destinationNode]);
  //   setSourceNode(null);
  //   console.log(informationFlows);
  // }, [sourceNode]);

  // / Export the information flows as a JSON file
  const exportInformationFlows = () => {
    const dataStr =
      "data:text/json;charset=utf-8," +
      encodeURIComponent(JSON.stringify(informationFlows));
    const downloadAnchorNode = document.createElement("a");
    downloadAnchorNode.setAttribute("href", dataStr);
    downloadAnchorNode.setAttribute("download", "informationFlows.json");
    document.body.appendChild(downloadAnchorNode);
    downloadAnchorNode.click();
    downloadAnchorNode.remove();
  };

  // Import the information flows from a JSON file
  const importInformationFlows = (event) => {
    const fileReader = new FileReader();
    fileReader.onload = (e) => {
      try {
        const importedFlows = JSON.parse(e.target.result);

        if (Array.isArray(importedFlows)) {
          console.log(importedFlows);
          console.log(informationFlows);
          debugger;
          // Convert the imported flows into nodes and update the state
          setInformationFlows(
            importedFlows.map(([sourceNode, destinationNode]) => {
              return [
                nodes.find((node) => node.id === sourceNode.id) || sourceNode,
                nodes.find((node) => node.id === destinationNode.id) ||
                destinationNode,
              ];
            })
          );
          alert("Information Flows loaded successfully.");
        } else {
          alert("Invalid JSON format.");
        }
      } catch (error) {
        alert("Failed to load the JSON file. Please check the file format.");
      }
    };

    if (event.target.files.length > 0) {
      fileReader.readAsText(event.target.files[0]);
    }
  };

  const findPath = (sourceId, targetId) => {
    const visited = new Set();
    const queue = [[sourceId]];

    while (queue.length > 0) {
      const path = queue.shift();
      const node = path[path.length - 1];

      if (node === targetId) {
        return path;
      }

      if (!visited.has(node)) {
        visited.add(node);
        edges
          .filter((edge) => edge.source === node || edge.target === node)
          .forEach((edge) => {
            const nextNode = edge.source === node ? edge.target : edge.source;
            queue.push([...path, nextNode]);
          });
      }
    }
    return [];
  };

  const handleNodeClick = (event, nodeData) => {
    const id = nodeData.id;
    const node = nodes.find((n) => n.id === id);
    setSelectedNode(node);
    const selectedInformationFlow = informationFlows?.find(flow => flow.find(flowId => flowId?.id == nodeData.id))?.find(infoFlow => infoFlow?.id == nodeData?.id)
    setSelectedInformationFlow(selectedInformationFlow)

    if (enableInformationFlow) {
      setResetFlow(false);
      if (!sourceNode) {
        setSourceNode(node);
        setNodes((nds) =>
          nds.map((n) =>
            n.id === node.id ? { ...n, style: { border: "2px solid blue" } } : n
          )
        );
      } else if (sourceNode.id !== id) {
        // setDestinationNodes((prevDestinations) => {
        //   if (!prevDestinations.some((dest) => dest.id === node.id)) {
        //     // const newDestinations = [...prevDestinations, node];
        //     // newDestinations.forEach((destination) => {

        //     // });
        //     return newDestinations;
        //   }
        //   return prevDestinations;
        // });
        const path = findPath(sourceNode.id, node.id);
        setEdges((eds) =>
          eds.map((edge) => {
            console.log(edge);
            const isInPath =
              path.includes(edge.source) && path.includes(edge.target);
            return {
              ...edge,
              animated:
                edge?.style?.stroke == "green" || isInPath ? true : false,
              style: {
                stroke:
                  edge?.style?.stroke == "green"
                    ? "green"
                    : isInPath
                      ? "green"
                      : "black",
              },
              markerEnd: {
                type:
                  edge?.style?.stroke == "green" || isInPath
                    ? MarkerType.ArrowClosed
                    : null,
                color: "green",
                width: 20,
                height: 20,
              },
            };
          })
        );

        setDestinationNode(node);
        let informationFl = [...informationFlows];

        // Check if the source-destination pair already exists
        const isDuplicate = informationFl.some(
          (flow) => flow[0].id === sourceNode.id && flow[1].id === node.id
        );

        // Only add the flow if it doesn't already exist
        if (!isDuplicate) {
          informationFl.push([sourceNode, node]);
          setInformationFlows(informationFl);
        }
        //information flow is added here [s]
        setNodes((nds) =>
          nds.map((n) =>
            n.id === node.id
              ? { ...n, style: { border: "2px solid green" } }
              : n
          )
        );
        setTimeout(resetSelection, 1000);
        // resetSelection();
      }
    }
  };

  const showSelectedHolonFlows = () => {
    if (!selectedNode) {
      alert("No holon selected!");
      return;
    }
  
    if (!informationFlows || informationFlows.length === 0) {
      console.error("No information flows available.");
      return;
    }
  
    // Filter information flows for the selected holon
    const selectedFlows = informationFlows.filter(
      ([source, destination]) =>
        source?.id === selectedNode?.id || destination?.id === selectedNode?.id
    );
  
    if (!selectedFlows || selectedFlows.length === 0) {
      console.error("No flows found for the selected holon.");
      return;
    }
  
    // Highlight flows on the graph
    selectedFlows.forEach(([sourceNode, destinationNode]) => {
      if (sourceNode && destinationNode) {
        showOneFlowOnGraph(sourceNode, destinationNode);
      }
    });
  
    // Update nodes and edges with validation
    setNodes((nds) =>
      nds.map((node) =>
        selectedFlows.some(([source, destination]) => 
          source?.id === node?.id || destination?.id === node?.id
        )
          ? node
          : { ...node, style: { border: "none" } }
      )
    );
  
    setEdges((eds) =>
      eds.map((edge) => {
        const isInSelectedFlow = selectedFlows.some(
          ([source, destination]) =>
            (edge.source === source?.id && edge.target === destination?.id) ||
            (edge.target === source?.id && edge.source === destination?.id)
        );
  
        return {
          ...edge,
          animated: isInSelectedFlow,
          style: { stroke: isInSelectedFlow ? "green" : "black" },
          markerEnd: isInSelectedFlow
            ? { type: MarkerType.ArrowClosed, color: "green" }
            : null,
        };
      })
    );
  };
  
  

  const showInformationFlowOnTheGraphs = () => {
    // write the code to use the information flows 2d array,
    // iterate through the information flow 2d array one by one,
    // read the source node and destination node which will be 1 array, source node is the 0th index element and destination node is 1st index element
    // the source node color should be blue and destination source node should be green and the edge color will be green and dash animation.
    informationFlows.forEach((flow) => {
      const [sourceNode, destinationNode] = flow;
      showOneFlowOnGraph(sourceNode, destinationNode);
    });
  };
  const showOneFlowOnGraph = (sourceNode, destinationNode) => {
    // Reset the graph to clear any existing styles
    // resetGraphs();

    // Update the nodes to show the selected source and destination colors
    setNodes((nds) =>
      nds.map((n) =>
        n.id === sourceNode.id
          ? { ...n, style: { border: "2px solid blue" } }
          : n.id === destinationNode.id
            ? { ...n, style: { border: "2px solid green" } }
            : n
      )
    );

    // Find the path from sourceNode to destinationNode
    const path = findPath(sourceNode.id, destinationNode.id);

    if (!path || path.length === 0) {
      console.error("No path found between the selected nodes.");
      return;
    }

    // Update edges with green color, dashed animation, and correct arrow direction
    setEdges((eds) =>
      eds.map((edge) => {
        const isInPath =
          path.includes(edge.source) && path.includes(edge.target);

        // Determine if the edge is in the forward or backward direction
        const isForwardDirection =
          edge.source === path[0] && edge.target === path[1];
        const isBackwardDirection =
          edge.target === path[0] && edge.source === path[1];

        // Ensure edge.style and markerEnd are initialized
        return {
          ...edge,
          animated: isInPath ? true : edge.animated,
          style: {
            ...edge.style, // Merge existing styles
            stroke: isInPath ? "green" : edge.style?.stroke,
            strokeDasharray: isInPath ? "5,5" : edge.style?.strokeDasharray,
          },
          markerEnd: isInPath
            ? {
              type: MarkerType.ArrowClosed,
              color: "green",
              orient: isForwardDirection ? "auto" : "auto-start-reverse",
            }
            : edge.markerEnd,
        };
      })
    );
  };

  const resetSelection = () => {
    setEnableInformationFlow(!enableInformationFlow);
    if (enableInformationFlow) {
      setResetFlow(true);
      setSourceNode(null);
      setDestinationNode(null);
      // setSelectedNode(null);
      setNodes((nds) => nds.map((n) => ({ ...n, style: { border: "none" } })));
      setEdges((eds) =>
        eds.map((edge) => ({
          ...edge,
          animated: false,
          style: { stroke: "black" },
        }))
      );
    }
  };

  const resetGraphs = () => {
    setNodes((nds) => nds.map((n) => ({ ...n, style: { border: "none" } })));
    setEdges((eds) =>
      eds.map((edge) => ({
        ...edge,
        animated: false,
        style: { stroke: "black" },
      }))
    );
  };

  return (
    <div
      className="container-fluid"
      style={{ display: "flex", height: "100vh", width: '100vw', }}
    >
      <div className="col-9" style={{ height: "100%" }}>
        <div className="d-flex justify-content-between align-items-center mb-3" style={{ position: 'absolute', top: -88, right: '-23vw' }}>
          {/* <h3>Add Node</h3> */}
          <button
            className={
              "btn btn-success"
            }
            onClick={resetSelection}
          >
            Add Information Flow
          </button>
          {/* {informationFlows.length > 0 && (
            <button
              className={"btn btn-success"}
              onClick={showInformationFlowOnTheGraphs}
            >
              Show Information Flows
            </button>
          )} */}
          {informationFlows.length > 0 && (
            <button
              className={"btn btn-primary ml-2"}
              onClick={exportInformationFlows}
            >
              Export Information Flows
            </button>
          )}

          <input
            type="file"
            accept=".json"
            onChange={importInformationFlows}
            style={{ display: "none", }}
            id="uploadFile"
          />
          <label htmlFor="uploadFile" className="btn btn-secondary ml-2 mt-2">
            Import Information Flows
          </label>
        </div>

        <ReactFlow
          nodeTypes={nodeTypes}
          nodes={nodes.map((node) => ({
            ...node,
            data: {
              ...node.data,
              disable: disableAdd,
            },
          }))}
          edges={edges}
          onNodesChange={onNodesChange}
          onEdgesChange={onEdgesChange}
          onConnect={onConnect}
          fitView
          fitViewOptions={fitViewOptions}
          style={{ width: "100%", height: "90%" }}
          onNodeClick={handleNodeClick}
          onPaneClick={() => setSelectedNode(null)}
        />
      </div>
      {sourceNode && destinationNode == null ? (
        <div style={{ position: "fixed", top: "90%", left: "20%" }}>
          <div className="mt-3 alert alert-danger">
            Select the Destination Node
          </div>
        </div>
      ) : (
        ""
      )}
      {resetFlow & enableInformationFlow ? (
        <div style={{ position: "fixed", top: "90%", left: "20%" }}>
          <div className="mt-3 alert alert-danger">Select the Source Node</div>
        </div>
      ) : (
        ""
      )}
      <div
  className="col-3"
  style={{ padding: "10px", borderLeft: "1px solid #ccc" }}
>
  {selectedNode ? (
    <div className="mr-3">
      <h3>Holon Details</h3>
      <div className="mb-4" style={{ borderBottom: "1px solid #ccc", paddingBottom: "10px" }}>
        <div className="form-group mb-3">
          <label htmlFor="nameofNode">Name</label>
          <input
            type="text"
            id="nameofNode"
            className="form-control"
            value={selectedNode.data.label}
            onChange={(e) =>
              setNodes((nds) =>
                nds.map((node) =>
                  node.id === selectedNode.id
                    ? {
                        ...node,
                        data: { ...node.data, label: e.target.value },
                      }
                    : node
                )
              )
            }
          />
        </div>
        <div className="form-group mb-3">
          <label htmlFor="nodeDescription">Description</label>
          <textarea
            id="nodeDescription"
            className="form-control"
            style={{ height: "200px", width: "100%" }}
            placeholder="An example description..."
            value={selectedNode.data.description || ""}
            onChange={(e) =>
              setNodes((nds) =>
                nds.map((node) =>
                  node.id === selectedNode.id
                    ? {
                        ...node,
                        data: { ...node.data, description: e.target.value },
                      }
                    : node
                )
              )
            }
          />
        </div>
      </div>

      <h3>Information Flows</h3>
      <div>
        <div className="form-group mb-3">
          <label htmlFor="nodeInfoFlows">Flows Linked to this Holon</label>
          <ul>
            {informationFlows
              .filter(
                ([source, destination]) =>
                  source.id === selectedNode.id || destination.id === selectedNode.id
              )
              .map(([source, destination], index) => (
                <li key={index}>
                  {source.data.label} â†’ {destination.data.label}
                </li>
              ))}
          </ul>
        </div>
        <div className="form-group mb-3">
          <label htmlFor="nodeInfoFlowsDescription">Information Flow Description</label>
          <textarea
            type="text"
            id="nodeInfoFlowsDescription"
            className="form-control"
            style={{ height: "100px", width: "100%" }}
            value={
              informationFlows.length > 0
                ? selectedInformationFlow?.data?.description || "No description available"
                : selectedNode.data.description || ""
            }
            readOnly
          />
        </div>
        <div>
          <button
            className={"btn btn-success mx-1"}
            onClick={() => showInformationFlowOnTheGraphs()}
          >
            Show All Flows
          </button>
          <button
            className={"btn btn-success mx-1"}
            onClick={() => showSelectedHolonFlows()}
          >
            Show Selected Holon Flows
          </button>
          {informationFlows.length > 0 && (
            <button
              className={"btn btn-primary mx-1"}
              onClick={resetGraphs}
            >
              Reset Flows
            </button>
          )}
        </div>
      </div>
    </div>
  ) : (
    <div>No Selection</div>
  )}
</div>

      <Modal isOpen={isModalOpen} onRequestClose={() => setIsModalOpen(false)}>
        <div className="modal-header">
          <h5 className="modal-title">Add Node</h5>
          <button
            type="button"
            className="close"
            onClick={() => setIsModalOpen(false)}
          >
            <span>&times;</span>
          </button>
        </div>
        <div className="modal-body">
          <div className="form-group mb-3">
            <label htmlFor="newNodeLabel">New Node Label</label>
            <input
              type="text"
              id="newNodeLabel"
              className="form-control"
              placeholder="New Node Label"
              value={newNodeLabel}
              onChange={(e) => setNewNodeLabel(e.target.value)}
            />
          </div>
        </div>
        <div className="modal-footer">
          <button
            type="button"
            className="btn btn-secondary"
            onClick={() => setIsModalOpen(false)}
          >
            Close
          </button>
          <button
            type="button"
            className="btn btn-primary"
            onClick={() => {
              setNodes((nds) => [
                ...nds,
                {
                  id: `${nds.length + 1}`,
                  type: "customNode",
                  data: { label: newNodeLabel },
                  position: { x: Math.random() * 400, y: Math.random() * 400 },
                },
              ]);
              setIsModalOpen(false);
              setNewNodeLabel("");
            }}
          >
            Add Node
          </button>
        </div>
      </Modal>
    </div>
  );
}

export default CommunicationReactFlow;
