import React, { useState } from 'react';
import { Form, Button, Container, Row, Col, ProgressBar, Card, Tooltip, OverlayTrigger, Modal } from 'react-bootstrap';
import emailjs from 'emailjs-com';

const IdeaSubmissionForm = () => {
  const [progress, setProgress] = useState(0);
  const [showModal, setShowModal] = useState(false);

  const handleInputChange = () => {
    const filledFields = document.querySelectorAll(
      'input:required:valid, textarea:required:valid'
    ).length;
    const totalFields = document.querySelectorAll('input:required, textarea:required').length;
    const newProgress = Math.round((filledFields / totalFields) * 100);
    setProgress(newProgress);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Send form data to company email using emailjs
    emailjs.sendForm('YOUR_SERVICE_ID', 'YOUR_TEMPLATE_ID', e.target, 'YOUR_USER_ID')
      .then((response) => {
        console.log('SUCCESS!', response.status, response.text);
        setShowModal(true);
      })
      .catch((err) => {
        console.error('FAILED...', err);
        alert('An error occurred while sending the form. Please try again.');
      });
  };

  return (
    <div className="background-video-container d-flex justify-content-center align-items-center min-vh-100">
      <video autoPlay loop muted className="background-video">
        <source src="/mnt/data/vid.mp4" type="video/mp4" />
        Your browser does not support the video tag.
      </video>
      <Container className="my-5 form-container shadow rounded p-5">
        {/* Greeting Section */}
        <div className="greeting-section mb-5">
          <h1 className="greeting-title">Welcome to the BugXTech Competition!</h1>
          <p className="greeting-text">
            We are thrilled to have you here. BugXTech's Idea Submission Competition is all about bringing simple, cost-effective, and resourceful ideas to life. We aim to solve real-world problems with impactful solutions.
          </p>
          <div className="greeting-details d-flex justify-content-between">
            <Card className="details-card" style={{ width: '48%' }}>
              <Card.Body>
                <Card.Title>Participant Goals</Card.Title>
                <ul className="greeting-list">
                  <li>Opportunities to <strong>win prizes</strong>.</li>
                  <li>Collaborate with <strong>startups in Germany</strong>.</li>
                  <li>Gain <strong>international exposure</strong>.</li>
                  <li>Access to <strong>mentorship from industry experts</strong>.</li>
                  <li>Build a <strong>strong professional network</strong> with peers and industry leaders.</li>
                  <li>Receive <strong>constructive feedback</strong> to improve your idea.</li>
                  <li>Get the opportunity to <strong>showcase your idea</strong> on a global platform.</li>
                </ul>
              </Card.Body>
            </Card>
            <Card className="points-info-card" style={{ width: '48%' }}>
              <Card.Body>
                <Card.Title>Evaluation Criteria</Card.Title>
                <Card.Text>
                  This competition is evaluated on a <strong>total of 100 points</strong>. Each section of the form is assigned specific points, and BugXTech will assess your submission based on the marks given for each part. Make sure to provide comprehensive and detailed responses to maximize your score.
                </Card.Text>
                <Card.Text>
                  <strong>Breakdown:</strong>
                  <ul className="points-breakdown-list">
                    <li>Personal Information: <strong>20 points</strong></li>
                    <li>Idea Information: <strong>50 points</strong></li>
                    <li>Collaboration: <strong>30 points</strong></li>
                  </ul>
                </Card.Text>
              </Card.Body>
            </Card>
          </div>
          <p className="cta-text mt-4">
            Apply now via LinkedIn and be part of the <a href="https://www.linkedin.com/company/bugxtech" target="_blank">BugXTech Competition</a>!
          </p>
        </div>
        
        {/* Form Section */}
        <h2 className="text-center mb-4 form-title">BugXTech Idea Submission Form</h2>
        <ProgressBar now={progress} label={`${progress}% Complete`} className="mb-4 progress-bar-custom" />
        
        {/* Personal Information Section */}
        <h3 className="section-title">1. Personal Information <span className="points-highlight-section">(20 points)</span></h3>
        <Form onSubmit={handleSubmit}>
          <Row>
            <Col md={6}>
              <Form.Group controlId="name">
                <Form.Label className="form-label">Name <span className="points-highlight-field">(5 points)</span></Form.Label>
                <Form.Control type="text" name="name" placeholder="Enter your name" required className="form-input" onChange={handleInputChange} />
              </Form.Group>
            </Col>

            <Col md={6}>
              <Form.Group controlId="email">
                <Form.Label className="form-label">Email Address <span className="points-highlight-field">(5 points)</span></Form.Label>
                <Form.Control type="email" name="email" placeholder="Enter your email address" required className="form-input" onChange={handleInputChange} />
              </Form.Group>
            </Col>
          </Row>

          {/* Idea Information Section */}
          <h3 className="section-title mt-5">2. Idea Information <span className="points-highlight-section">(50 points)</span></h3>
          <Form.Group controlId="ideaTitle" className="mt-4">
            <Form.Label className="form-label">Idea Title <span className="points-highlight-field">(5 points)</span></Form.Label>
            <OverlayTrigger
              placement="right"
              overlay={<Tooltip id="tooltip-idea-title">Provide a concise title that best represents your idea.</Tooltip>}
            >
              <Form.Control type="text" name="ideaTitle" placeholder="Enter your idea title" required className="form-input" onChange={handleInputChange} />
            </OverlayTrigger>
          </Form.Group>

          <Form.Group controlId="ideaDescription" className="mt-4">
            <Form.Label className="form-label">Idea Description <span className="points-highlight-field">(15 points)</span></Form.Label>
            <div className="character-count" id="idea-description-counter" style={{ textAlign: 'right', fontSize: '0.9rem', color: '#6c757d' }}>Remaining characters: 500</div>
            <Form.Control as="textarea" name="ideaDescription" rows={3} maxLength={500} placeholder="Describe your idea in detail (Max 500 characters)" required className="form-textarea" onChange={(e) => {
              handleInputChange();
              const remainingChars = 500 - e.target.value.length;
              const counterElement = document.getElementById('idea-description-counter');
              if (counterElement) {
                counterElement.innerText = `Remaining characters: ${remainingChars}`;
              }
            }} />
          </Form.Group>

          <Form.Group controlId="targetAudience" className="mt-4">
            <Form.Label className="form-label">Target Audience <span className="points-highlight-field">(10 points)</span></Form.Label>
            <Form.Control as="textarea" name="targetAudience" rows={3} placeholder="Who are the customers for this idea?" required className="form-input" onChange={handleInputChange} />
            <div className="mt-2" style={{ fontSize: '0.9rem', color: '#6c757d' }}>Show your support by using the BugXTech competition hashtag on your LinkedIn profile.</div>
          </Form.Group>

          <Form.Group controlId="problemStatement" className="mt-4">
            <Form.Label className="form-label">Problem Statement <span className="points-highlight-field">(10 points)</span></Form.Label>
            <Form.Control as="textarea" name="problemStatement" rows={3} placeholder="What problem does this idea solve for the customers?" required className="form-textarea" onChange={handleInputChange} />
          </Form.Group>

          <Form.Group controlId="potentialImpact" className="mt-4">
            <Form.Label className="form-label">Potential Impact <span className="points-highlight-field">(5 points)</span></Form.Label>
            <Form.Control as="textarea" name="potentialImpact" rows={3} placeholder="How will this idea impact the customers?" required className="form-textarea" onChange={handleInputChange} />
          </Form.Group>

          {/* Collaboration Section */}
          <h3 className="section-title mt-5">3.
 Collaboration <span className="points-highlight-section">(30 points)</span></h3>
          <Form.Group controlId="collaborationInterest" className="mt-4">
            <Form.Label className="form-label">Collaboration Interest <span className="points-highlight-field">(10 points)</span></Form.Label>
            <Form.Control as="textarea" name="collaborationInterest" rows={3} placeholder="Are you interested in collaborating with others on this idea?" className="form-textarea" onChange={handleInputChange} />
          </Form.Group>

          <Row>
            <Col md={6}>
              <Form.Group controlId="linkedin" className="mt-4">
                <Form.Label className="form-label">LinkedIn Profile Links (optional) <span className="points-highlight-field">(0 points)</span></Form.Label>
                <Form.Control type="text" name="linkedin" placeholder="Enter your LinkedIn profile link" className="form-input" onChange={handleInputChange} />
              </Form.Group>
            </Col>
            <Col md={6}>
              <Form.Group controlId="hashtagUsage" className="mt-4">
                <Form.Label className="form-label">Hashtag Usage <span className="points-highlight-field">(10 points)</span></Form.Label>
                <Form.Control type="text" name="hashtagUsage" placeholder="Show your support by using the BugXTech competition hashtag on your LinkedIn profile." required className="form-input" onChange={handleInputChange} />
              </Form.Group>
            </Col>
          </Row>

          {/* LinkedIn Share Section */}
          <Form.Group controlId="linkedinShare" className="mt-4">
            <Form.Label className="form-label">LinkedIn Share Confirmation <span className="points-highlight-field">(10 points)</span></Form.Label>
            <div className="form-check-group">
              <Form.Check 
                type="radio"
                label="Yes, I confirm I will like a post from BugXTech on LinkedIn and share it to my network, tagging BugXTech."
                name="linkedinShareOptions"
                value="yes"
                className="form-check"
                onChange={handleInputChange}
              />
              <Form.Check 
                type="radio"
                label="No, I do not wish to share on LinkedIn (Optional, though sharing is encouraged for broader engagement)."
                name="linkedinShareOptions"
                value="no"
                className="form-check"
                onChange={handleInputChange}
              />
            </div>
          </Form.Group>

          {/* Trust Factor */}
          <div className="mt-5">
            <p className="text-muted trust-text" style={{ fontSize: '1.1rem', fontWeight: 'bold' }}>
              <strong>Trust Factor:</strong> We value your privacy. Rest assured that your idea will not be shared with any third parties and will only be used internally for evaluation and collaboration.
            </p>
          </div>

          {/* Submit Button */}
          <Button variant="primary" type="submit" className="mt-4 submit-button">Submit Your Idea</Button>
        </Form>

        {/* Acknowledgment Modal */}
        <Modal show={showModal} onHide={() => setShowModal(false)}>
          <Modal.Header closeButton>
            <Modal.Title>Thank You!</Modal.Title>
          </Modal.Header>
          <Modal.Body>Your idea has been successfully submitted. We appreciate your participation in the BugXTech competition!</Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={() => setShowModal(false)}>
              Close
            </Button>
          </Modal.Footer>
        </Modal>
      </Container>
    </div>
  );
};

export default IdeaSubmissionForm;

