import React, { useState } from 'react';
import ReactFlow, { addEdge, Background, MiniMap, Controls } from 'react-flow-renderer';

const App = () => {
  const [elements, setElements] = useState([
    { id: 'root', type: 'input', data: { label: 'Information System' }, position: { x: 250, y: 25 }, style: { border: '2px solid #000', borderRadius: '8px', padding: '20px', background: '#fff', width: '200px' }},
    { id: 'stakeholder', data: { label: 'Stakeholder' }, position: { x: 100, y: 150 }, style: { border: '2px solid #000', borderRadius: '8px', padding: '20px', background: '#fff', width: '150px' }},
    { id: 'infrastructure', data: { label: 'Infrastructure' }, position: { x: 400, y: 150 }, style: { border: '2px solid #000', borderRadius: '8px', padding: '20px', background: '#fff', width: '150px' }},
    { id: 'stakeholder-edge', source: 'root', target: 'stakeholder', animated: true, type: 'smoothstep', arrowHeadType: 'arrowclosed' },
    { id: 'infrastructure-edge', source: 'root', target: 'infrastructure', animated: true, type: 'smoothstep', arrowHeadType: 'arrowclosed' },
  ]);

  return (
    <ReactFlow
      elements={elements}
      style={{ height: '100vh', backgroundColor: '#f8f8f8' }}
      onConnect={(params) => setElements((els) => addEdge(params, els))}
      snapToGrid={true}
      snapGrid={[15, 15]}
    >
      <Background color="#f8f8f8" gap={16} />
      <MiniMap
        nodeColor={(node) => {
          switch (node.type) {
            case 'input':
              return 'blue';
            case 'default':
              return '#FFCC00';
            case 'output':
              return 'green';
            default:
              return '#eee';
          }
        }}
      />
      <Controls />
    </ReactFlow>
  );
};

export default App;
