import React, { useState } from 'react';
import './styles.css';

const Holon = ({ name, onSelect, onAdd, onRemove }) => {
  return (
    <div className="holon" onClick={() => onSelect(name)}>
      <div className="name">{name}</div>
      <button className="add-button" onClick={(e) => onAdd(e, name)}>+</button>
      <button className="remove-button" onClick={(e) => onRemove(e, name)}>x</button>
    </div>
  );
};

const App = () => {
  const [nodes, setNodes] = useState([
    {
      id: 'root',
      name: 'Information System',
      description: 'An Information System is a set of components for collecting, storing, and processing data.',
      children: [],
    }
  ]);
  const [selectedHolon, setSelectedHolon] = useState(null);
  const [idCounter, setIdCounter] = useState(1); // Added for generating unique IDs

  const selectHolon = (name) => {
    const holon = findNode(nodes, name);
    setSelectedHolon(holon);
  };

  const addChild = (e, parentId) => {
    e.stopPropagation(); // Stop the event from bubbling up to the parent
    const childName = prompt('Enter the name of the child:');
    if (childName === null || childName.trim() === '') {
      alert('Please enter a valid name for the child.');
      return;
    }

    setNodes((prevNodes) => {
      const newNodes = [...prevNodes];
      const addNodeRecursively = (nodes) => {
        nodes.forEach((node) => {
          if (node.id === parentId) {
            const newId = `node-${idCounter}`; // Generate a unique ID based on a counter
            const newChild = { id: newId, name: childName, description: '', children: [] };
            node.children.push(newChild);
            setIdCounter(idCounter + 1); // Increment the counter for the next unique ID
          } else if (node.children.length) {
            addNodeRecursively(node.children);
          }
        });
      };

      addNodeRecursively(newNodes);
      return newNodes;
    });
  };

  const removeChild = (e, nodeName) => {
    e.stopPropagation();
    setNodes((prevNodes) => removeNodeRecursively([...prevNodes], nodeName));
  };

  const findNode = (nodes, name) => nodes.reduce((acc, node) => {
    if (acc) return acc;
    if (node.name === name) return node;
    return findNode(node.children, name);
  }, null);

  const removeNodeRecursively = (nodes, name) => nodes.filter(node => {
    if (node.name === name) return false; // Do not include the node to be removed
    node.children = removeNodeRecursively(node.children, name); // Recurse into children
    return true;
  });

  return (
    <div className="app-container">
      <div className="holons-container">
        {nodes.map((node) => (
          <div key={node.id}>
            <Holon name={node.name} onSelect={selectHolon} onAdd={(e) => addChild(e, node.id)} onRemove={(e) => removeChild(e, node.name)} />
            {node.children.length > 0 && (
              <div className="children">
                {node.children.map((child) => (
                  <Holon key={child.id} name={child.name} onSelect={selectHolon} onAdd={(e) => addChild(e, child.id)} onRemove={(e) => removeChild(e, child.name)} />
                ))}
              </div>
            )}
          </div>
        ))}
      </div>
      <div className="holon-details">
        {selectedHolon ? (
          <>
            <h2>Holon Details</h2>
            <p>Name: {selectedHolon.name}</p>
            <p>Description: {selectedHolon.description}</p>
          </>
        ) : (
          <p>Select a holon to see details.</p>
        )}
      </div>
    </div>
  );
};

export default App;
