import React, { useState, useEffect } from 'react';
import './styles.css';

const Holon = ({ node, onSelect, onAdd, onRemove, level }) => {
  return (
    <div className={`holon holon-level-${level}`} onClick={() => onSelect(node)}>
      <div className="holon-details">
        <div className="name">{node.name}</div>
        <div className="buttons">
          <button className="add-button" onClick={(e) => { e.stopPropagation(); onAdd(node); }}>+</button>
          <button className="remove-button" onClick={(e) => { e.stopPropagation(); onRemove(node.id); }}>x</button>
        </div>
      </div>
    </div>
  );
};

const App = () => {
  const [nodes, setNodes] = useState([
    // Your initial nodes structure...
  ]);
  const [selectedHolon, setSelectedHolon] = useState(null);
  const [idCounter, setIdCounter] = useState(3); 

  useEffect(() => {
    // No change needed here, initial state setup
  }, []);

  const flattenNodes = (nodes, level = 0, parentId = null) => {
    return nodes.reduce((acc, node) => {
      const newNode = { ...node, level, parentId };
      acc.push(newNode);
      if (node.children) {
        acc = acc.concat(flattenNodes(node.children, level + 1, node.id));
      }
      return acc;
    }, []);
  };

  const selectHolon = (node) => {
    setSelectedHolon(node);
  };

  const addChild = (parentNode) => {
    const childName = prompt('Enter the name of the child:');
    if (!childName || childName.trim() === '') {
      alert('Please enter a valid name for the child.');
      return;
    }

    const newId = `node-${idCounter}`;
    const newNode = { id: newId, name: childName, description: '', children: [] };

    const addNodeRecursively = (nodes, newNode, parentId) => {
      return nodes.map(node => {
        if (node.id === parentId) {
          node.children = [...node.children, newNode];
        } else if (node.children.length) {
          node.children = addNodeRecursively(node.children, newNode, parentId);
        }
        return node;
      });
    };

    setNodes(nodes => addNodeRecursively(nodes, newNode, parentNode.id));
    setIdCounter(idCounter + 1);
  };

  const removeChild = (nodeId) => {
    const removeNodeRecursively = (nodes, nodeId) => {
      return nodes.reduce((acc, node) => {
        if (node.id !== nodeId) {
          if (node.children) {
            node.children = removeNodeRecursively(node.children, nodeId);
            acc.push(node);
          } else {
            acc.push(node);
          }
        }
        return acc;
      }, []);
    };

    setNodes(nodes => removeNodeRecursively(nodes, nodeId));
  };

  const flattenedNodes = flattenNodes(nodes);

  return (
    <div className="app-container">
      <div className="holons-container">
        {flattenedNodes.map(node => (
          <Holon key={node.id} node={node} onSelect={selectHolon} onAdd={addChild} onRemove={removeChild} level={node.level} />
        ))}
      </div>
      {selectedHolon && (
        <div className="holon-details">
          <h2>Holon Details</h2>
          <p>Name: {selectedHolon.name}</p>
          <p>Description: {selectedHolon.description || "No description provided."}</p>
        </div>
      )}
    </div>
  );
};

export default App;
