import React, { useState } from "react";
import { Tree, TreeNode } from "react-organizational-chart";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faPlus, faTrash } from "@fortawesome/free-solid-svg-icons";
import "bootstrap/dist/css/bootstrap.min.css";
import Button from "react-bootstrap/Button";
import Modal from "react-bootstrap/Modal";
import Form from "react-bootstrap/Form";
import ToggleButton from "react-bootstrap/ToggleButton";
import ToggleButtonGroup from "react-bootstrap/ToggleButtonGroup";
import "./styles.css";
import CommunicationPage from "./CommunicationPage";
import CommunicationReactFlow from "./CommunicationReactFlow";
import InfrastructureReactFlow from "./InfraStructureReactFlow";
import ReactFlow, { addEdge, useNodesState, useEdgesState } from "reactflow";

// Updated node positions and corrected IDs
const initNodes = [
  { 
    id: "1", 
    type: "customNode", 
    data: { label: "Information System",
       level: 0 }, 
       description:
       "An information system is an organized combination of people, hardware, software, communication networks, and data resources that collects, transforms, and disseminates information in an organization.",
    position: { x: 0, y: 0 } 
  },
  { 
    id: "2", 
    type: "customNode", 
    data: { label: "Stakeholder", level: 1 }, 
    position: { x: 100, y: 100 } 
  },
  { 
    id: "3", 
    type: "customNode", 
    data: { label: "Infrastructure", level: 1 }, 
    position: { x: -100, y: 100 } 
  },
  // Add more nodes for testing same-level connections
];

const initEdges = [
  { id: "e1-2", source: "1", target: "2" },
  { id: "e1-3", source: "1", target: "3" },
];

const Holon = ({
  node,
  onAddChild,
  onRemoveNode,
  onSelectNode,
  isSelected,
}) => {
  const handleActionClick = (e, action) => {
    e.stopPropagation();
    action();
  };

  const selected = isSelected(node);

  return (
    <TreeNode
      label={
        <div
          className={`p-2 mb-2 border ${
            selected ? "tree-label selected" : "tree-label"
          } rounded`}
          onClick={() => onSelectNode(node)}
        >
          <div>{node.name}</div>
          <div className="d-flex justify-content-end">
            <Button
              variant="primary"
              size="sm"
              className="me-1"
              onClick={(e) => handleActionClick(e, () => onAddChild(node))}
            >
              <FontAwesomeIcon icon={faPlus} />
            </Button>
            <Button
              variant="danger"
              size="sm"
              onClick={(e) => handleActionClick(e, () => onRemoveNode(node.id))}
            >
              <FontAwesomeIcon icon={faTrash} />
            </Button>
          </div>
        </div>
      }
    >
      {node.children &&
        node.children.map((child) => (
          <Holon
            key={child.id}
            node={child}
            onAddChild={onAddChild}
            onRemoveNode={onRemoveNode}
            onSelectNode={onSelectNode}
            isSelected={isSelected}
          />
        ))}
    </TreeNode>
  );
};

const App = () => {
  const [activeStep, setActiveStep] = useState(1);
  const [nodes, setNodes, onNodesChange] = useNodesState(initNodes);
  const [edges, setEdges, onEdgesChange] = useEdgesState(initEdges);

  return (
    <div className="container-fluid" style={{ overflowX: "hidden" }}>
      <div className="d-flex justify-content-start align-items-center p-3 bg-light shadow-sm">
        {/* Stepper component remains unchanged */}
        <div
          className={`step me-3 ${activeStep === 1 ? "active" : ""}`}
          onClick={() => setActiveStep(1)}
        >
          <div
            className={`step-circle text-white rounded-circle d-flex justify-content-center align-items-center ${
              activeStep === 1 ? "bg-primary" : "bg-secondary"
            }`}
            style={{ width: "30px", height: "30px" }}
          >
            1
          </div>
          <div className={`ms-2 fw-bold ${activeStep === 1 ? "text-primary" : ""}`}>
            Structure
          </div>
        </div>
        <div
          className={`step ${activeStep === 2 ? "active" : ""}`}
          onClick={() => setActiveStep(2)}
        >
          <div
            className={`step-circle text-white rounded-circle d-flex justify-content-center align-items-center ${
              activeStep === 2 ? "bg-primary" : "bg-secondary"
            }`}
            style={{ width: "30px", height: "30px" }}
          >
            2
          </div>
          <div className={`ms-2 fw-bold ${activeStep === 2 ? "text-primary" : ""}`}>
            Communication
          </div>
        </div>
      </div>

      <div className="row p-3">
        <div className="col-md-8" style={{ height: "80vh" }}>
          {activeStep === 1 ? (
            <InfrastructureReactFlow
              nodes={nodes}
              edges={edges}
              onNodesChange={onNodesChange}
              onEdgesChange={onEdgesChange}
              setNodes={setNodes}     // Pass setNodes to enable adding nodes
              setEdges={setEdges}     // Pass setEdges if needed
            />
          ) : (
            <CommunicationReactFlow
              nodes={nodes}
              edges={edges}
              onNodesChange={onNodesChange}
              onEdgesChange={onEdgesChange}
              setNodes={setNodes}
              setEdges={setEdges}
            />
          )}
        </div>
      </div>
    </div>
  );
};

export default App;
