import React, { useState, useCallback } from 'react';
import ReactFlow, {
  ReactFlowProvider,
  addEdge,
  Controls,
  Background,
} from 'react-flow-renderer';
import 'react-flow-renderer/dist/style.css';
import './styles.css';

const Alert = ({ message }) => {
  return (
    <div className="alert">
      {message}
    </div>
  );
};

const initialElements = [
  {
    id: '1',
    type: 'input', // Input node type
    data: { label: 'Holon 1A' },
    position: { x: 250, y: 0 },
  },
  {
    id: '2',
    type: 'default',
    data: { label: 'Holon 2A' },
    position: { x: 150, y: 150 },
  },
  {
    id: '3',
    type: 'default',
    data: { label: 'Holon 2B' },
    position: { x: 350, y: 150 },
  },
  {
    id: '4',
    type: 'default',
    data: { label: 'Holon 3A' },
    position: { x: 150, y: 300 },
  },
  {
    id: '5',
    type: 'default',
    data: { label: 'Holon 3B' },
    position: { x: 350, y: 300 },
  },
  {
    id: 'e1-2',
    source: '1',
    target: '2',
    type: 'smoothstep',
    animated: false,
    style: { stroke: '#000', strokeWidth: 1, strokeDasharray: '5,5' },
  },
  {
    id: 'e1-3',
    source: '1',
    target: '3',
    type: 'smoothstep',
    animated: false,
    style: { stroke: '#000', strokeWidth: 1, strokeDasharray: '5,5' },
  },
  {
    id: 'e2-4',
    source: '2',
    target: '4',
    type: 'smoothstep',
    animated: false,
    style: { stroke: '#000', strokeWidth: 1, strokeDasharray: '5,5' },
  },
  {
    id: 'e3-5',
    source: '3',
    target: '5',
    type: 'smoothstep',
    animated: false,
    style: { stroke: '#000', strokeWidth: 1, strokeDasharray: '5,5' },
  },
];

const getNodeColor = (node, selectedNode, hoveredNode) => {
  if (node.id === selectedNode?.id) return '#00f'; // Blue for selected node
  if (node.id === hoveredNode?.id) return '#0f0'; // Green for hovered node
  return '#fff';
};

const CustomNode = ({ data }) => {
  return (
    <div className="node" style={{ backgroundColor: data.color }}>
      {data.label}
    </div>
  );
};

const nodeTypes = {
  special: CustomNode,
};

const CommunicationPage = () => {
  const [elements, setElements] = useState(initialElements);
  const [selectedNode, setSelectedNode] = useState(null);
  const [hoveredNode, setHoveredNode] = useState(null);
  const [isAddingFlow, setIsAddingFlow] = useState(false);

  const onConnect = useCallback(
    (params) =>
      setElements((els) =>
        addEdge(
          { ...params, type: 'smoothstep', animated: true, style: { stroke: 'green' } },
          els
        )
      ),
    []
  );

  const onNodeMouseEnter = useCallback(
    (_event, node) => {
      if (isAddingFlow) {
        setHoveredNode(node);
      }
    },
    [isAddingFlow]
  );

  const onNodeMouseLeave = useCallback(() => setHoveredNode(null), []);

  const onNodeClick = useCallback(
    (_event, node) => {
      if (isAddingFlow) {
        if (node.id !== selectedNode.id) {
          const newEdge = {
            id: `e${selectedNode.id}-${node.id}`,
            source: selectedNode.id,
            target: node.id,
            animated: true,
            style: { stroke: 'green' },
          };
          setElements((els) => addEdge(newEdge, els));
          setIsAddingFlow(false);
          setSelectedNode(null);
        }
      } else {
        setSelectedNode(node);
        setIsAddingFlow(true);
      }
    },
    [isAddingFlow, selectedNode]
  );

  const elementsWithColors = elements.map((el) => {
    if (el.type === 'default' || el.type === 'input') {
      return {
        ...el,
        data: { ...el.data, color: getNodeColor(el, selectedNode, hoveredNode) },
      };
    }
    return el;
  });

  return (
    <div className="app-container">
      <div className="steps-bar">
        <div className="step active">
          <div className="step-circle">1</div>
          <div className="step-label">Structure</div>
        </div>
        <div className="step">
          <div className="step-circle">2</div>
          <div className="step-label">Communication</div>
        </div>
      </div>
      <div className="content-area">
        <ReactFlowProvider>
          <ReactFlow
            elements={elementsWithColors}
            onConnect={onConnect}
            nodeTypes={nodeTypes}
            onNodeMouseEnter={onNodeMouseEnter}
            onNodeMouseLeave={onNodeMouseLeave}
            onNodeClick={onNodeClick}
            snapToGrid={true}
            snapGrid={[15, 15]}
          >
            <Controls />
            <Background color="#aaa" gap={16} />
          </ReactFlow>
          {isAddingFlow && <Alert message="Click another holon to add an information flow." />}
        </ReactFlowProvider>
        <div className="holon-details">
          {selectedNode ? (
            <>
              <h2>Holon Details</h2>
              <p><strong>Name:</strong> {selectedNode.data.label}</p>
              <p><strong>Description:</strong> {selectedNode.data.description || 'No description available.'}</p>
            </>
          ) : (
            <p>No Selection</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default CommunicationPage;
