import React, { useState, useEffect } from 'react';
import './styles.css';

const Holon = ({ node, onSelect, onAdd, onSameLevelAdd, onRemove, isSelected }) => {
  const { name, children } = node;

  // Adding click handler to toggle selection styling
  const handleSelect = (e, node) => {
    e.stopPropagation();
    onSelect(node);
  };

  return (
    <div className={`holon ${isSelected ? 'selected' : ''}`} onClick={(e) => handleSelect(e, node)}>
      <div className="name">{name}</div>
      <div className="buttons">
        <button className="add-button" onClick={(e) => { e.stopPropagation(); onAdd(node); }}>+</button>
        {children.length === 0 && (
          <button className="same-level-button" onClick={(e) => { e.stopPropagation(); onSameLevelAdd(node); }}>Same Level +</button>
        )}
        <button className="remove-button" onClick={(e) => { e.stopPropagation(); onRemove(node.name); }}>x</button>
      </div>
      {children.length > 0 && (
        <div className="children">
          {children.map((child) => (
            <Holon key={child.id} node={child} onSelect={onSelect} onAdd={onAdd} onSameLevelAdd={onSameLevelAdd} onRemove={onRemove} isSelected={isSelected?.id === child.id} />
          ))}
        </div>
      )}
    </div>
  );
};

