  };

  const removeChild = (nodeName) => {
    // Logic for removing a child node, similar to your existing logic
  };

  // UseEffect for handling selection style changes, if needed

  return (
    <div className="app-container">
      <div className="holons-container">
        {nodes.map((node) => (
          <Holon key={node.id} node={node} onSelect={selectHolon} onAdd={addChild} onSameLevelAdd={addSameLevelHolon} onRemove={removeChild} isSelected={selectedHolon?.id === node.id} />
        ))}
      </div>
      <div className="holon-details">
        {selectedHolon ? (
          <>
            <h2>Holon Details</h2>
            <p>Name: {selectedHolon.name}</p>
            <p>Description: {selectedHolon.description || "No description provided."}</p>
          </>
