import React from 'react';
import ReactFlow, { addEdge, Background, Controls, MiniMap } from 'react-flow-renderer';

const App = () => {
  const elements = [
    { id: '1', type: 'input', data: { label: 'Information System' }, position: { x: 250, y: 25 }, style: { border: '2px solid #000', borderRadius: '8px', padding: '20px', background: '#fff', width: '200px' }},
    { id: '2', data: { label: 'Stakeholder' }, position: { x: 100, y: 150 }, style: { border: '2px solid #000', borderRadius: '8px', padding: '20px', background: '#fff', width: '150px' }},
    { id: '3', data: { label: 'Infrastructure' }, position: { x: 400, y: 150 }, style: { border: '2px solid #000', borderRadius: '8px', padding: '20px', background: '#fff', width: '150px' }},
    { id: 'e1-2', source: '1', target: '2', animated: true, type: 'smoothstep', arrowHeadType: 'arrowclosed' },
    { id: 'e1-3', source: '1', target: '3', animated: true, type: 'smoothstep', arrowHeadType: 'arrowclosed' },
  ];

  return (
    <div style={{ height: '100vh', backgroundColor: '#f8f8f8' }}>
      <ReactFlow elements={elements} onConnect={(params) => console.log('edge added', params)} snapToGrid={true}>
        <Background color="#f8f8f8" gap={16} />
        <MiniMap
          nodeColor={(node) => {
            switch (node.type) {
              case 'input':
                return 'blue';
              case 'default':
                return '#FFCC00';
              case 'output':
                return 'green';
              default:
                return '#eee';
            }
          }}
        />
        <Controls />
      </ReactFlow>
    </div>
  );
};

export default App;
