import React, { useState } from 'react';
import Tree from 'react-d3-tree';
import './styles.css';

const Holon = ({ node, onSelect, onAdd, onRemove }) => {
  const { name, children } = node;

  return (
    <div className="holon-details" onClick={() => onSelect(node)}>
      <div className="name">{name}</div>
      <div className="buttons">
        <button className="add-button" onClick={(e) => { e.stopPropagation(); onAdd(name); }}>+</button>
        <button className="remove-button" onClick={(e) => { e.stopPropagation(); onRemove(name); }}>x</button>
      </div>
      {children.length > 0 && (
        <div className="children">
          {children.map(child => (
            <Holon key={child.id} node={child} onSelect={onSelect} onAdd={onAdd} onRemove={onRemove} />
          ))}
        </div>
      )}
    </div>
  );
};

const App = () => {
  const [nodes, setNodes] = useState([
    {
      name: 'Information System',
      description: 'An Information System is a set of components for collecting, storing, and processing data.',
      children: [
        {
          name: 'Stakeholder',
          description: 'A stakeholder refers to any individual, group, or organization that has an interest or concern in the system, its development, implementation, or outcomes. These stakeholders can be internal or external to the organization implementing the information system. Understanding and managing stakeholders is crucial for the success of an information system project.',
          children: [],
        },
        {
          name: 'Infrastructure',
          description: 'Infrastructure is the foundation or framework that supports a system or organization.',
          children: [],
        }
      ],
    }
  ]);
  const [selectedHolon, setSelectedHolon] = useState(null);

  const selectHolon = (selectedNode) => {
    setSelectedHolon(selectedNode);
  };

  const addChild = (parentName) => {
    const childName = prompt('Enter the name of the child:');
    if (!childName || childName.trim() === '') {
      alert('Please enter a valid name for the child.');
      return;
    }

    setNodes(prevNodes => {
      const parentNode = findNode(prevNodes[0], parentName);
      if (parentNode) {
        const newNode = {
          name: childName,
          description: '',
          children: [],
        };
        parentNode.children.push(newNode);
      }
      return [...prevNodes];
    });
  };

  const removeChild = (nodeName) => {
    setNodes(prevNodes => removeNode(prevNodes[0], nodeName));
  };

  const findNode = (node, name) => {
    if (node.name === name) {
      return node;
    } else if (node.children) {
      for (let child of node.children) {
        const foundNode = findNode(child, name);
        if (foundNode) {
          return foundNode;
        }
      }
    }
    return null;
  };

  const removeNode = (node, nodeName) => {
    if (!node.children) return node;
    node.children = node.children.filter(child => child.name !== nodeName);
    node.children.forEach(child => removeNode(child, nodeName));
    return node;
  };

  return (
    <div className="app-container">
      <div className="graph-container">
        <Tree
          data={nodes[0]}
          orientation="vertical"
          translate={{ x: 100, y: 50 }}
          nodeSvgShape={{ shape: 'none' }}
          nodeLabelComponent={{
            render: <Holon onSelect={selectHolon} onAdd={addChild} onRemove={removeChild} />,
            foreignObjectWrapper: {
              width: 200,
              x: -100,
              y: -25,
            },
          }}
        />
      </div>
      <div className="holon-details">
        {selectedHolon ? (
          <>
            <h2>Holon Details</h2>
            <p>Name: {selectedHolon.name}</p>
            <p>Description: {selectedHolon.description || "No description provided."}</p>
          </>
        ) : (
          <p>Select a holon to see details.</p>
        )}
      </div>
    </div>
  );
};

export default App;
