import React, { useState, useEffect } from 'react';
import './styles.css';

const Holon = ({ node, onSelect, onAdd, onRemove }) => {
  return (
    <div className={`holon holon-level-${node.level}`} onClick={() => onSelect(node)}>
      <div className="holon-details">
        <div className="name">{node.name}</div>
        <div className="buttons">
          <button className="add-button" onClick={(e) => { e.stopPropagation(); onAdd(node); }}>+</button>
          <button className="remove-button" onClick={(e) => { e.stopPropagation(); onRemove(node.id); }}>x</button>
        </div>
      </div>
    </div>
  );
};

const flattenNodes = (nodes, level = 0) => {
  return nodes.reduce((acc, node) => {
    const newNode = { ...node, level, children: [] }; // Clear children to ensure independent rendering
    acc.push(newNode);
    if (node.children) {
      acc = acc.concat(flattenNodes(node.children, level + 1));
    }
    return acc;
  }, []);
};

const App = () => {
  const initialNodes = [
    {
      id: 'root',
      name: 'Information System',
      description: 'An Information System is a set of components for collecting, storing, and processing data.',
      children: [
        {
          id: 'stakeholder',
          name: 'Stakeholder',
          description: 'A stakeholder refers to any individual, group, or organization that has an interest or concern in the system.',
          children: [],
        },
        {
          id: 'infrastructure',
          name: 'Infrastructure',
          description: 'Infrastructure is the foundation that supports a system.',
          children: [],
        },
      ],
    },
  ];

  const [nodes, setNodes] = useState(initialNodes);
  const [selectedHolon, setSelectedHolon] = useState(null);
  const [idCounter, setIdCounter] = useState(3);
  const [flattenedNodes, setFlattenedNodes] = useState([]);

  useEffect(() => {
    setFlattenedNodes(flattenNodes(nodes));
  }, [nodes]);

  const selectHolon = (node) => setSelectedHolon(node);

  // Implement addChild and removeChild based on your logic to modify the state

  return (
    <div className="app-container">
      {flattenedNodes.map(node => (
        <Holon key={node.id} node={node} onSelect={selectHolon} onAdd={() => {}} onRemove={() => {}} />
      ))}
      {selectedHolon && (
        <div className="holon-details">
          <h2>Holon Details</h2>
          <p>Name: {selectedHolon.name}</p>
          <p>Description: {selectedHolon.description || "No description provided."}</p>
        </div>
      )}
    </div>
  );
};

export default App;
