import React, { useState } from 'react';
import { Modal, Button } from 'react-bootstrap';
import { Tree, TreeNode } from 'react-organizational-chart';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlus, faTrash } from '@fortawesome/free-solid-svg-icons';
import 'bootstrap/dist/css/bootstrap.min.css';

const Holon = ({ node, onAddChild, onRemoveNode, onSelectNode, isSelected }) => {
    const handleActionClick = (e, action) => {
        e.stopPropagation();
        action();
    };

    const selected = isSelected(node);

    return (
        <TreeNode
            label={
                <div className={`tree-label ${selected ? 'bg-primary text-white' : 'bg-light'}`} onClick={() => onSelectNode(node)}>
                    <div className="d-flex align-items-center justify-content-between">
                        <div>{node.name}</div>
                        <div>
                            <Button variant="outline-primary" size="sm" className="me-1" onClick={(e) => handleActionClick(e, () => onAddChild(node, true))}>
                                <FontAwesomeIcon icon={faPlus} />
                            </Button>
                            <Button variant="outline-danger" size="sm" onClick={(e) => handleActionClick(e, () => onRemoveNode(node.id))}>
                                <FontAwesomeIcon icon={faTrash} />
                            </Button>
                        </div>
                    </div>
                </div>
            }
        >
            {node.children && node.children.map(child => (
                <Holon
                    key={child.id}
                    node={child}
                    onAddChild={onAddChild}
                    onRemoveNode={onRemoveNode}
                    onSelectNode={onSelectNode}
                    isSelected={isSelected}
                />
            ))}
        </TreeNode>
    );
};

const StructurePage = ({ nodes, onAddChild, onRemoveNode, onSelectNode, isSelected }) => {
    const [showModal, setShowModal] = useState(false);

    const handleClose = () => setShowModal(false);
    const handleShow = () => setShowModal(true);

    return (
        <div className="container mt-4">
            <h1>Structure Page</h1>
            <div className="tree-container mt-4">
                <Tree lineWidth={"2px"} lineColor={"blue"} lineBorderRadius={"5px"} lineStyle={"dashed"}>
                    <Holon
                        node={nodes}
                        onAddChild={onAddChild}
                        onRemoveNode={onRemoveNode}
                        onSelectNode={onSelectNode}
                        isSelected={(node) => isSelected && node.id === isSelected.id}
                    />
                </Tree>
            </div>

            <Button variant="primary" onClick={handleShow} className="mt-4">
                Launch Modal
            </Button>

            <Modal show={showModal} onHide={handleClose} size="lg">
                <Modal.Header closeButton>
                    <Modal.Title>Modal Title</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <p>Additional information or interactions can be placed here.</p>
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={handleClose}>
                        Close
                    </Button>
                    <Button variant="primary" onClick={handleClose}>
                        Save Changes
                    </Button>
                </Modal.Footer>
            </Modal>
        </div>
    );
};

export default StructurePage;
