import React, { useState } from 'react';
import './styles.css'; // Make sure styles are defined as needed

// Adjusted Holon Component to include conditional rendering of child interaction UI
const Holon = ({ name, children, onAddChild, onRemove, canInteract }) => {
  const [childName, setChildName] = useState('');

  const handleAddClick = () => {
    if (childName.trim() === '') {
      alert('Please enter a valid name for the child.');
      return;
    }
    onAddChild(name, childName);
    setChildName(''); // Reset input after adding
  };

  const handleInputChange = (event) => {
    setChildName(event.target.value);
  };

  return (
    <div className="holon">
      <div className="name">{name}</div>
      {/* Conditional rendering based on canInteract prop */}
      {canInteract && (
        <>
          <input
            value={childName}
            onChange={handleInputChange}
            placeholder="Child name"
          />
          <button className="add-button" onClick={handleAddClick}>+</button>
          <button className="remove-button" onClick={() => onRemove(name)}>x</button>
        </>
      )}
      {children.map((child, index) => (
        <div key={index} className="child-container">
          <Holon
            name={child.name}
            children={child.children}
            onAddChild={onAddChild}
            onRemove={onRemove}
            canInteract={true} // Enable interaction for child nodes
          />
        </div>
      ))}
    </div>
  );
};

// App Component managing nodes
const App = () => {
  const [nodes, setNodes] = useState([
    { name: 'Information System', children: [] },
    { name: 'Stakeholder', children: [] }, // Added as independent nodes
    { name: 'Infrastructure', children: [] } // Added as independent nodes
  ]);

  const addNode = (parentName, childName) => {
    const newNode = { name: childName, children: [] };
    setNodes((prevNodes) => {
      const updateNodes = (nodes, parentName, newNode) => {
        return nodes.map((node) => {
          if (node.name === parentName) {
            return { ...node, children: [...node.children, newNode] };
          } else if (node.children.length) {
            return { ...node, children: updateNodes(node.children, parentName, newNode) };
          }
          return node;
        });
      };
      return updateNodes(prevNodes, parentName, newNode);
    });
  };

  const removeNode = (nodeName) => {
    const removeNodeByName = (nodes, nodeName) => {
      return nodes.reduce((acc, node) => {
        if (node.name === nodeName) return acc;
        const children = removeNodeByName(node.children, nodeName);
        return [...acc, { ...node, children }];
      }, []);
    };
    setNodes(prev => removeNodeByName(prev, nodeName));
  };

  return (
    <div className="container">
      {nodes.map((node, index) => (
        <Holon
          key={index}
          name={node.name}
          children={node.children}
          onAddChild={addNode}
          onRemove={removeNode}
          canInteract={index !== 0} // Disable interaction for the first node (Information System)
        />
      ))}
    </div>
  );
};

export default App;
