import React, { useState } from 'react';
import './styles.css';

const Holon = ({ name, onSelect, onAdd, onRemove }) => {
  // Prevent event propagation to avoid triggering onSelect when clicking buttons
  const handleAddClick = (e) => {
    e.stopPropagation();
    onAdd(name);
  };

  const handleRemoveClick = (e) => {
    e.stopPropagation();
    onRemove(name);
  };

  return (
    <div className="holon" onClick={() => onSelect(name)}>
      <div className="name">{name}</div>
      <button className="add-button" onClick={handleAddClick}>+</button>
      <button className="remove-button" onClick={handleRemoveClick}>x</button>
    </div>
  );
};

const App = () => {
  const [selectedHolon, setSelectedHolon] = useState(null);

  // Simplified structure for demonstration
  const holons = [
    {
      id: 'root',
      name: 'Information System',
      description: 'An Information System is a set of components for collecting, storing, and processing data.',
      children: [
        {
          id: 'stakeholder',
          name: 'Stakeholder',
          description: 'A stakeholder is an individual, group, or organization that has interest or concern in an organization.',
        },
        {
          id: 'infrastructure',
          name: 'Infrastructure',
          description: 'Infrastructure in an IT context is the physical hardware used to interconnect computers and users.',
        }
      ],
    }
  ];

  const selectHolon = (name) => {
    const holon = holons[0].children.find(holon => holon.name === name) || holons[0];
    setSelectedHolon(holon);
  };

  // Placeholder functions for adding and removing nodes
  const addChild = (parentName, childName) => {
    console.log(`Add child ${childName} to ${parentName}`);
    // Add logic to dynamically add children based on application needs
  };

  const removeChild = (parentName) => {
    console.log(`Remove ${parentName}`);
    // Add logic to dynamically remove children based on application needs
  };

  return (
    <div className="app-container">
      <div className="holons-container">
        <Holon name={holons[0].name} onSelect={selectHolon} onAdd={addChild} onRemove={removeChild} />
        <div className="children">
          {holons[0].children.map(child => (
            <Holon key={child.id} name={child.name} onSelect={selectHolon} onAdd={addChild} onRemove={removeChild} />
          ))}
        </div>
      </div>
      <div className="holon-details">
        {selectedHolon ? (
          <>
            <h2>Holon Details</h2>
            <p>Name: {selectedHolon.name}</p>
            <p>Description: {selectedHolon.description}</p>
          </>
        ) : (
          <p>Select a holon to see details</p>
        )}
      </div>
    </div>
  );
};

export default App;
