import React, { useState } from 'react';
import './App.css';

// Initial setup of the information system
const initialHolon = {
  id: 'root',
  name: 'Information System',
  description: 'Top-level entity of an information system.',
  children: [
    {
      id: 'stakeholder',
      name: 'Stakeholder',
      description: 'Represents an entity with interest in the system.',
      children: [],
    },
    {
      id: 'infrastructure',
      name: 'Infrastructure',
      description: 'Underlying technical structure of the system.',
      children: [],
    },
  ],
};

function App() {
  const [system, setSystem] = useState([initialHolon]);
  const [selectedHolon, setSelectedHolon] = useState(null);

  // Functionality for adding, selecting holons will be implemented here.

  return (
    <div className="app-container">
      {/* Left panel: Process View and Canvas View */}
      {/* Right panel: Tools View and Detail View */}
    </div>
  );
}

export default App;

const HolonNode = ({ holon, onSelect }) => {
  return (
    <div>
      <div onClick={() => onSelect(holon)}>{holon.name}</div>
      {holon.children.length > 0 && (
        <div className="children">
          {holon.children.map((child) => (
            <HolonNode key={child.id} holon={child} onSelect={onSelect} />
          ))}
        </div>
      )}
    </div>
  );
};
const DetailView = ({ holon }) => {
  if (!holon) return <div>No Selection</div>;

  return (
    <div>
      <h2>Holon Details</h2>
      <p>Name: {holon.name}</p>
      <p>Description: {holon.description}</p>
      {/* Extend with form inputs to allow editing */}
    </div>
  );
};
