import React, { useCallback, useState } from "react";
import ReactFlow, {
  addEdge,
  useNodesState,
  useEdgesState,
  MarkerType,
} from "reactflow";
import Modal from "react-modal";
import "reactflow/dist/style.css";
import "bootstrap/dist/css/bootstrap.min.css";
import "./styles.css";
import CustomNodeFlow from "./CustomNodeFlow";

const nodeTypes = {
  customNode: CustomNodeFlow,
};

const fitViewOptions = { padding: 0.5 };

Modal.setAppElement("#root");

function CommunicationReactFlow(props) {
  const [nodes, setNodes, onNodesChange] = useNodesState(props.nodes);
  const [edges, setEdges, onEdgesChange] = useEdgesState(props.edges);
  const [sourceNode, setSourceNode] = useState(null);
  const [destinationNodes, setDestinationNodes] = useState([]);
  const [selectedNode, setSelectedNode] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [enableInformationFlow, setEnableInformationFlow] = useState(false);
  const [newNodeLabel, setNewNodeLabel] = useState("");
  const [disableAdd, setDisableAdd] = useState(false);
  const [resetFlow, setResetFlow] = useState(false);
  const onConnect = useCallback(
    (params) => setEdges((eds) => addEdge(params, eds)),
    [setEdges]
  );

  const findPath = (sourceId, targetId) => {
    const visited = new Set();
    const queue = [[sourceId]];

    while (queue.length > 0) {
      const path = queue.shift();
      const node = path[path.length - 1];

      if (node === targetId) {
        return path;
      }

      if (!visited.has(node)) {
        visited.add(node);
        edges
          .filter((edge) => edge.source === node || edge.target === node)
          .forEach((edge) => {
            const nextNode = edge.source === node ? edge.target : edge.source;
            queue.push([...path, nextNode]);
          });
      }
    }
    return [];
  };

  const handleNodeClick = (event, nodeData) => {
    const id = nodeData.id;
    const node = nodes.find((n) => n.id === id);
    setSelectedNode(node);

    if (enableInformationFlow) {
      setResetFlow(false);
      if (!sourceNode) {
        setSourceNode(node);
        setNodes((nds) =>
          nds.map((n) =>
            n.id === node.id ? { ...n, style: { border: "2px solid blue" } } : n
          )
        );
      } else if (sourceNode.id !== id) {
        setDestinationNodes((prevDestinations) => {
          if (!prevDestinations.some((dest) => dest.id === node.id)) {
            const newDestinations = [...prevDestinations, node];
            newDestinations.forEach((destination) => {
              const path = findPath(sourceNode.id, destination.id);
              setEdges((eds) =>
                eds.map((edge) => {
                  console.log(edge);
                  const isInPath =
                    path.includes(edge.source) && path.includes(edge.target);
                  return {
                    ...edge,
                    animated:
                      edge?.style?.stroke == "green" || isInPath ? true : false,
                    style: {
                      stroke:
                        edge?.style?.stroke == "green"
                          ? "green"
                          : isInPath
                          ? "green"
                          : "red",
                    },
                    markerEnd: {
                      type:
                        edge?.style?.stroke == "red" || isInPath
                          ? MarkerType.ArrowClosed
                          : null,
                      color: "green",
                      width: 20,
                      height: 20,
                    },
                  };
                })
              );
            });
            return newDestinations;
          }
          return prevDestinations;
        });
        setNodes((nds) =>
          nds.map((n) =>
            n.id === node.id
              ? { ...n, style: { border: "2px solid green" } }
              : n
          )
        );
      }
    }
  };

  const resetSelection = () => {
    setEnableInformationFlow(!enableInformationFlow);
    if (enableInformationFlow) {
      setResetFlow(true);
      setSourceNode(null);
      setDestinationNodes([]);
      setSelectedNode(null);
      setNodes((nds) => nds.map((n) => ({ ...n, style: { border: "none" } })));
      setEdges((eds) =>
        eds.map((edge) => ({
          ...edge,
          animated: false,
          style: { stroke: "black" },
        }))
      );
    }
  };

  return (
    <div
      className="container-fluid"
      style={{ display: "flex", height: "100vh" }}
    >
      <div className="col-9" style={{ height: "100%" }}>
        <div className="d-flex justify-content-between align-items-center mb-3">
          <h3>Add Node</h3>
          <button
            className={
              !enableInformationFlow ? "btn btn-danger" : "btn btn-success"
            }
            onClick={resetSelection}
          >
            Add Information Flow
          </button>
        </div>

        <ReactFlow
          nodeTypes={nodeTypes}
          nodes={nodes.map((node) => ({
            ...node,
            data: {
              ...node.data,
              disable: disableAdd,
            },
          }))}
          edges={edges}
          onNodesChange={onNodesChange}
          onEdgesChange={onEdgesChange}
          onConnect={onConnect}
          fitView
          fitViewOptions={fitViewOptions}
          style={{ width: "100%", height: "90%" }}
          onNodeClick={handleNodeClick}
        />
      </div>
      {sourceNode && destinationNodes.length === 0 ? (
        <div style={{ position: "fixed", top: "90%", left: "20%" }}>
          <div className="mt-3 alert alert-danger">
            Select the Destination Node
          </div>
        </div>
      ) : (
        ""
      )}
      {resetFlow & enableInformationFlow ? (
        <div style={{ position: "fixed", top: "90%", left: "20%" }}>
          <div className="mt-3 alert alert-danger">Select the Source Node</div>
        </div>
      ) : (
        ""
      )}
      <div
        className="col-3"
        style={{ padding: "10px", borderLeft: "1px solid #ccc" }}
      >
        {selectedNode ? (
          <>
            <h3>Holon Details</h3>
            <div className="form-group mb-3">
              <label htmlFor="nameofNode">Name</label>
              <input
                type="text"
                id="nameofNode"
                className="form-control"
                value={selectedNode.data.label}
                onChange={(e) =>
                  setNodes((nds) =>
                    nds.map((node) =>
                      node.id === selectedNode.id
                        ? {
                            ...node,
                            data: { ...node.data, label: e.target.value },
                          }
                        : node
                    )
                  )
                }
              />
            </div>
            <div className="form-group mb-3">
              <label htmlFor="nodeDescription">Description</label>
              <textarea
                id="nodeDescription"
                className="form-control"
                style={{ height: "200px", width: "400px" }}
                placeholder="An example description..."
                value={selectedNode.data.description || ""}
                onChange={(e) =>
                  setNodes((nds) =>
                    nds.map((node) =>
                      node.id === selectedNode.id
                        ? {
                            ...node,
                            data: { ...node.data, description: e.target.value },
                          }
                        : node
                    )
                  )
                }
              />
            </div>
            <div className="form-group mb-3">
              <label htmlFor="nodeInfoFlows">Information Flows</label>
              <input
                type="text"
                id="nodeInfoFlows"
                className="form-control"
                value={selectedNode.data.label}
                readOnly
              />
            </div>
            {/* <div className="mt-3 alert alert-info">
              <strong>Node Selected:</strong> {selectedNode.data.label}
            </div> */}
            <div>
              {enableInformationFlow && (
                <p className="lead">Selected Information Flow</p>
              )}
            </div>
            {enableInformationFlow &&
              sourceNode &&
              destinationNodes &&
              destinationNodes.map((element) => {
                return (
                  <div className="mt-3 alert alert-success">
                    <strong>
                      {sourceNode.data.label} {"-->"} {element.data.label}
                    </strong>
                  </div>
                );
              })}
          </>
        ) : (
          <div>No Selection</div>
        )}
      </div>
      <Modal isOpen={isModalOpen} onRequestClose={() => setIsModalOpen(false)}>
        <div className="modal-header">
          <h5 className="modal-title">Add Node</h5>
          <button
            type="button"
            className="close"
            onClick={() => setIsModalOpen(false)}
          >
            <span>&times;</span>
          </button>
        </div>
        <div className="modal-body">
          <div className="form-group mb-3">
            <label htmlFor="newNodeLabel">New Node Label</label>
            <input
              type="text"
              id="newNodeLabel"
              className="form-control"
              placeholder="New Node Label"
              value={newNodeLabel}
              onChange={(e) => setNewNodeLabel(e.target.value)}
            />
          </div>
        </div>
        <div className="modal-footer">
          <button
            type="button"
            className="btn btn-secondary"
            onClick={() => setIsModalOpen(false)}
          >
            Close
          </button>
          <button
            type="button"
            className="btn btn-primary"
            onClick={() => {
              setNodes((nds) => [
                ...nds,
                {
                  id: `${nds.length + 1}`,
                  type: "customNode",
                  data: { label: newNodeLabel },
                  position: { x: Math.random() * 400, y: Math.random() * 400 },
                },
              ]);
              setIsModalOpen(false);
              setNewNodeLabel("");
            }}
          >
            Add Node
          </button>
        </div>
      </Modal>
    </div>
  );
}

export default CommunicationReactFlow;
