import React, { useState, useCallback } from 'react';
import ReactFlow, { MiniMap, Controls, Background, Handle, Position } from 'reactflow';
import 'reactflow/dist/style.css';
import dagre from 'dagre';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlus, faTrash } from '@fortawesome/free-solid-svg-icons';
import 'bootstrap/dist/css/bootstrap.min.css';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';
import Form from 'react-bootstrap/Form';
import ToggleButton from 'react-bootstrap/ToggleButton';
import ToggleButtonGroup from 'react-bootstrap/ToggleButtonGroup';
import './styles.css';
import CommunicationPage from './CommunicationPage';

const dagreGraph = new dagre.graphlib.Graph();
dagreGraph.setDefaultEdgeLabel(() => ({}));

const nodeWidth = 200;
const nodeHeight = 100;

const getLayoutedElements = (nodes, edges, direction = 'TB') => {
  const isHorizontal = direction === 'LR';
  dagreGraph.setGraph({ rankdir: direction });

  nodes.forEach((node) => {
    dagreGraph.setNode(node.id, { width: nodeWidth, height: nodeHeight });
  });

  edges.forEach((edge) => {
    dagreGraph.setEdge(edge.source, edge.target);
  });

  dagre.layout(dagreGraph);

  nodes.forEach((node) => {
    const nodeWithPosition = dagreGraph.node(node.id);
    node.targetPosition = isHorizontal ? Position.Left : Position.Top;
    node.sourcePosition = isHorizontal ? Position.Right : Position.Bottom;

    // We are shifting the dagre node position (anchor=center center) to the top left
    // so it matches the React Flow node anchor point (top left).
    node.position = {
      x: nodeWithPosition.x - nodeWidth / 2,
      y: nodeWithPosition.y - nodeHeight / 2,
    };

    return node;
  });

  return { nodes, edges };
};

const Holon = ({ node, onAddChild, onRemoveNode, onSelectNode, isSelected }) => {
  const handleActionClick = (e, action) => {
    e.stopPropagation();
    action();
  };

  const selected = isSelected(node);

  return (
    <div className={`p-2 mb-2 border ${selected ? 'tree-label selected' : 'tree-label'} rounded`} onClick={() => onSelectNode(node)}>
      <div>{node.name}</div>
      <div className="d-flex justify-content-end">
        <Button variant="primary" size="sm" className="me-1" onClick={(e) => handleActionClick(e, () => onAddChild(node, true))}>
          <FontAwesomeIcon icon={faPlus} />
        </Button>
        <Button variant="danger" size="sm" onClick={(e) => handleActionClick(e, () => onRemoveNode(node.id))}>
          <FontAwesomeIcon icon={faTrash} />
        </Button>
      </div>
    </div>
  );
};

const StructurePage = ({ nodes, onAddChild, onRemoveNode, onSelectNode, selectedNode }) => {
  const isSelected = (node) => selectedNode && node.id === selectedNode.id;

  const generateElements = (nodes) => {
    const elements = [];

    const traverse = (node, parent = null) => {
      elements.push({
        id: node.id,
        data: {
          label: <Holon
            node={node}
            onAddChild={onAddChild}
            onRemoveNode={onRemoveNode}
            onSelectNode={onSelectNode}
            isSelected={isSelected}
          />
        },
        position: { x: 0, y: 0 },
        type: 'default',
        style: { width: nodeWidth, height: nodeHeight }
      });

      if (parent) {
        elements.push({
          id: `${parent.id}-${node.id}`,
          source: parent.id,
          target: node.id,
          type: 'smoothstep'
        });
      }

      node.children && node.children.forEach(child => traverse(child, node));
    };

    traverse(nodes);
    return elements;
  };

  const elements = generateElements(nodes);

  const layoutedElements = getLayoutedElements(elements.filter(e => e.type === 'default'), elements.filter(e => e.type !== 'default'));

  return (
    <div style={{ height: '600px' }}>
      <ReactFlow elements={[...layoutedElements.nodes, ...layoutedElements.edges]} nodesDraggable={false} nodesConnectable={false}>
        <MiniMap />
        <Controls />
        <Background />
      </ReactFlow>
    </div>
  );
};

const App = () => {
  const initialNodes = {
    id: 'root',
    name: 'Information System',
    description: 'An information system is an organized combination of people, hardware, software, communication networks, and data resources that collects, transforms, and disseminates information in an organization.',
    children: [
      {
        id: 'Stakeholder',
        name: 'Stakeholder',
        description: 'A stakeholder refers to any individual, group, or organization that has an interest or concern in the system, its development, implementation, or outcomes. These stakeholders can be internal or external to the organization implementing the information system. Understanding and managing stakeholders is crucial for the success of an information system project.',
        children: [],
      },
      {
        id: 'Infrastructure',
        name: 'Infrastructure',
        description: 'Infrastructure in the context of information systems refers to the fundamental physical and organizational structures, facilities, and services required for the operation of an information system. This includes hardware, software, networking components, data centers, and the personnel that maintain and manage the system. Effective infrastructure supports the smooth and efficient collection, storage, processing, and dissemination of information.',
        children: [],
      }
    ],
  };

  const [nodes, setNodes] = useState(initialNodes);
  const [selectedNode, setSelectedNode] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [nodeType, setNodeType] = useState('');
  const [nodeName, setNodeName] = useState('');
  const [activeStep, setActiveStep] = useState(1);

  const handleModalClose = () => {
    setShowModal(false);
    setNodeName('');
  };

  const handleModalShow = () => {
    setShowModal(true);
  };

  const onAddChild = (parentNode) => {
    setSelectedNode(parentNode);
    setNodeType('child');
    handleModalShow();
  };

  const onRemoveNode = (nodeId) => {
    const removeNode = (nodes, nodeIdToRemove) => {
      if (nodes.id === nodeIdToRemove) {
        return null;
      }
      nodes.children = nodes.children.filter(child => child.id !== nodeIdToRemove);
      nodes.children.forEach(child => removeNode(child, nodeIdToRemove));
      return nodes;
    };

    const newNodes = removeNode({ ...nodes }, nodeId);
    setNodes(newNodes);
    setSelectedNode(null);
  };

  const addNewRoot = () => {
    setSelectedNode(nodes);
    setNodeType('parent');
    handleModalShow();
  };

  const handleNodeTypeSelection = (type) => {
    setNodeType(type);
  };

  const handleSubmit = () => {
    const newNode = {
      id: `${selectedNode.id}-${Math.random()}`,
      name: nodeName,
      children: []
    };
    if (nodeType === 'child') {
      const addNode = (nodes, parentId) => {
        if (nodes.id === parentId) {
          nodes.children.push(newNode);
          return;
        }
        nodes.children.forEach(child => addNode(child, parentId));
      };

      const newNodes = { ...nodes };
      addNode(newNodes, selectedNode.id);
      setNodes(newNodes);
    } else if (nodeType === 'parent') {
      const newParent = {
        id: `root-${Math.random()}`,
        name: nodeName,
        children: [selectedNode]
      };
      setNodes(newParent);
    }
    handleModalClose();
  };

  return (
    <div className="container-fluid">
      <div className="d-flex justify-content-start align-items-center p-3 bg-light shadow-sm">
        <div className={`step me-3 ${activeStep === 1 ? 'active' : ''}`} onClick={() => setActiveStep(1)}>
          <div className={`step-circle bg-secondary text-white rounded-circle d-flex justify-content-center align-items-center ${activeStep === 1 ? 'bg-primary' : ''}`} style={{ width: '30px', height: '30px' }}>1</div>
          <div className="ms-2 fw-bold">Structure</div>
        </div>
        <div className={`step ${activeStep === 2 ? 'active' : ''}`} onClick={() => setActiveStep(2)}>
          <div className={`step-circle bg-secondary text-white rounded-circle d-flex justify-content-center align-items-center ${activeStep === 2 ? 'bg-primary' : ''}`} style={{ width: '30px', height: '30px' }}>2</div>
          <div className="ms-2 fw-bold">Communication</div>
        </div>
      </div>
      <div className="row p-3">
        <div className="col-md-8">
          {activeStep === 1 ? (
            <StructurePage
              nodes={nodes}
              onAddChild={onAddChild}
              onRemoveNode={onRemoveNode}
              onSelectNode={setSelectedNode}
              selectedNode={selectedNode}
            />
          ) : (
            <div>Communication Page</div> // Placeholder for the CommunicationPage component
          )}
        </div>
        <div className="col-md-4">
          <h2>Holon Details</h2>
          {selectedNode ? (
            <>
              <p><strong>Name:</strong> {selectedNode.name}</p>
              <p><strong>Description:</strong> {selectedNode.description || "No description available."}</p>
            </>
          ) : (
            <p>No Selection</p>
          )}
        </div>
      </div>
      <Modal show={showModal} onHide={handleModalClose}>
        <Modal.Header closeButton>
          <Modal.Title>Select Node Type</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <ToggleButtonGroup
            type="radio"
            name="nodeType"
            value={nodeType}
            onChange={(value) => handleNodeTypeSelection(value)}
            className="mb-3"
          >
            <ToggleButton value="child" variant="outline-primary">Child Node</ToggleButton>
            <ToggleButton value="parent" variant="outline-primary">Parent Node</ToggleButton>
          </ToggleButtonGroup>
          {nodeType && (
            <Form.Group controlId="formNodeName">
              <Form.Label>Enter the name for the Node</Form.Label>
              <Form.Control type="text" placeholder="Node name" value={nodeName} onChange={(e) => setNodeName(e.target.value)} />
            </Form.Group>
          )}
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleModalClose}>
            Close
          </Button>
          <Button variant="primary" onClick={handleSubmit} disabled={!nodeType}>
            Save
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
};

export default App;
