import React, { useState } from 'react';
import './App.css';

// Define Holon component
const Holon = ({ node, onSelect, onAdd, onRemove }) => {
  return (
    <>
      <div className="holon">
        <div className="holon-details" onClick={() => onSelect(node)}>
          <div className="name">{node.name}</div>
          <div className="buttons">
            <button className="add-button" onClick={(e) => { e.stopPropagation(); onAdd(node.id); }}>+</button>
            {node.id !== 'root' && <button className="remove-button" onClick={(e) => { e.stopPropagation(); onRemove(node.id); }}>x</button>}
          </div>
        </div>
      </div>
      {node.children && node.children.length > 0 && (
        <div className="children-container">
          {node.children.map(child => (
            <Holon key={child.id} node={child} onSelect={onSelect} onAdd={onAdd} onRemove={onRemove} />
          ))}
        </div>
      )}
    </>
  );
};

const App = () => {
  const [nodes, setNodes] = useState({
    id: 'root',
    name: 'Information System',
    description: 'An Information System is a set of components for collecting, storing, and processing data.',
    children: [],
  });
  const [selectedHolon, setSelectedHolon] = useState(null);
  const [idCounter, setIdCounter] = useState(1);

  const selectHolon = (node) => {
    setSelectedHolon(node);
  };

  const addChild = (parentId) => {
    const childName = prompt('Enter the name of the child:');
    if (!childName || childName.trim() === '') {
      alert('Please enter a valid name for the child.');
      return;
    }
    const newNode = {
      id: `node-${idCounter}`,
      name: childName,
      description: '',
      children: []
    };
    setIdCounter(idCounter + 1);

    const addNodeRecursively = (node, parentId) => {
      if (node.id === parentId) {
        node.children = [...node.children, newNode];
        return;
      }
      node.children.forEach(child => addNodeRecursively(child, parentId));
    };

    const newNodes = { ...nodes };
    addNodeRecursively(newNodes, parentId);
    setNodes(newNodes);
  };

  const removeChild = (nodeId) => {
    const removeNodeRecursively = (node, nodeId) => {
      node.children = node.children.filter(child => child.id !== nodeId);
      node.children.forEach(child => removeNodeRecursively(child, nodeId));
    };

    const newNodes = { ...nodes };
    if (newNodes.id === nodeId) return; // Prevent deleting the root node
    removeNodeRecursively(newNodes, nodeId);
    setNodes(newNodes);
  };

  return (
    <div className="app-container">
      <div className="holons-container">
        <Holon node={nodes} onSelect={selectHolon} onAdd={addChild} onRemove={removeChild} />
      </div>
      <div className="holon-details">
        {selectedHolon ? (
          <>
            <h2>Holon Details</h2>
            <p>Name: {selectedHolon.name}</p>
            <p>Description: {selectedHolon.description || 'No description provided.'}</p>
          </>
        ) : (
          <p>Select a holon to see details.</p>
        )}
      </div>
    </div>
  );
};

export default App;
