import React, { useState } from 'react';
import { Tree, TreeNode } from 'react-organizational-chart';
import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import './styles.css';

const Alert = ({ message }) => {
    return (
        <div className="alert">
            {message}
        </div>
    );
};

const Holon = ({ node, onSelectNode, isSelected, setSelectedHolonIds, hoveredHolonId, setHoveredHolonId, showAddSign, setShowAddSign, showRegulationLine, toggleAddMessage }) => {
    const handleNodeClick = () => {
        setSelectedHolonIds([node.id]); // Select the Holon
        setShowAddSign(true); // Show the add signs on all Holons
        toggleAddMessage(false); // Hide the message
    };

    const handleAddClick = (e) => {
        e.stopPropagation();
        setSelectedHolonIds([node.id]); // Select the Holon
        setShowAddSign(false); // Hide the add signs from all Holons
        toggleAddMessage(true); // Show the message
    };

    const handleMouseEnter = () => {
        setHoveredHolonId(node.id); // Set the hovered Holon
    };

    const handleMouseLeave = () => {
        setHoveredHolonId(null); // Clear the hovered Holon
    };

    const selected = isSelected(node);
    const isHovered = hoveredHolonId === node.id && !selected; // Prevent green hover effect when selected

    return (
        <TreeNode
            label={
                <div
                    className={`tree-label ${selected ? 'selected' : ''} ${isHovered ? 'hovered' : ''}`}
                    onClick={handleNodeClick}
                    onMouseEnter={handleMouseEnter}
                    onMouseLeave={handleMouseLeave}
                >
                    <div className="name">{node.name}</div>
                    {showAddSign && (
                        <div className="buttons">
                            <button className="add-button" onClick={handleAddClick}>
                                +
                            </button>
                        </div>
                    )}
                </div>
            }
        >
            {node.children &&
                node.children.map((child) => (
                    <Holon
                        key={child.id}
                        node={child}
                        onSelectNode={onSelectNode}
                        isSelected={isSelected}
                        setSelectedHolonIds={setSelectedHolonIds}
                        hoveredHolonId={hoveredHolonId}
                        setHoveredHolonId={setHoveredHolonId}
                        showAddSign={showAddSign} // Show add sign on all Holons
                        setShowAddSign={setShowAddSign}
                        showRegulationLine={showRegulationLine}
                        toggleAddMessage={toggleAddMessage} // Pass down function to toggle the message
                    />
                ))}
            {showRegulationLine && node.id === 'Information System' && (
                <line x1="50%" y1="50%" x2="50%" y2="100%" className="regulation-channel" />
            )}
        </TreeNode>
    );
};

const CommunicationPage = () => {
    const initialNodes = {
        id: 'root',
        name: 'Information System',
        children: [
            {
                id: 'Stakeholder',
                name: 'Stakeholder',
                children: [],
            },
            {
                id: 'Infrastructure',
                name: 'Infrastructure',
                children: [],
            }
        ],
    };

    const [selectedHolonIds, setSelectedHolonIds] = useState([]);
    const [hoveredHolonId, setHoveredHolonId] = useState(null);
    const [showAddSign, setShowAddSign] = useState(true);
    const [showRegulationLine, setShowRegulationLine] = useState(false);
    const [showAddMessage, setShowAddMessage] = useState(false); // Define showAddMessage state
    const [showModal, setShowModal] = useState(false); // State for controlling the modal
    const [nodeName, setNodeName] = useState(''); // State for the node name input

    const toggleAddMessage = (value) => {
        setShowAddMessage(value); // Toggle the message
    };

    const handleSelectNode = (node) => {
        setSelectedHolonIds([node.id]);
        setShowAddSign(true); // Show the add signs on all Holons
    };

    const isSelected = (node) => selectedHolonIds.includes(node.id);

    const handleModalClose = () => {
        setShowModal(false);
        setNodeName('');
    };

    const handleModalShow = () => {
        setShowModal(true);
    };

    const handleAddClick = (e) => {
        e.stopPropagation();
        handleModalShow(); // Show the modal
    };

    const handleSubmit = () => {
        // Handle the addition of a new node
        handleModalClose();
    };

    return (
        <div className="app-container">
            <div className="content-area">
                <Tree lineWidth={"2px"} lineColor={"blue"} lineBorderRadius={"5px"} lineStyle={"dashed"}>
                    <Holon
                        node={initialNodes}
                        onSelectNode={handleSelectNode}
                        isSelected={isSelected}
                        setSelectedHolonIds={setSelectedHolonIds}
                        hoveredHolonId={hoveredHolonId}
                        setHoveredHolonId={setHoveredHolonId}
                        showAddSign={showAddSign}
                        setShowAddSign={setShowAddSign}
                        showRegulationLine={showRegulationLine}
                        toggleAddMessage={toggleAddMessage} // Pass down function to toggle the message
                    />
                </Tree>
            </div>
            {showAddMessage && <Alert message="Click another holon to add an information flow." />}
            
            <Modal show={showModal} onHide={handleModalClose}>
                <Modal.Header closeButton>
                    <Modal.Title>Add New Node</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <Form.Group controlId="formNodeName">
                        <Form.Label>Enter the name for the Node</Form.Label>
                        <Form.Control type="text" placeholder="Node name" value={nodeName} onChange={(e) => setNodeName(e.target.value)} />
                    </Form.Group>
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={handleModalClose}>
                        Close
                    </Button>
                    <Button variant="primary" onClick={handleSubmit} disabled={!nodeName}>
                        Save
                    </Button>
                </Modal.Footer>
            </Modal>
        </div>
    );
};

export default CommunicationPage;
