import React, { useState } from 'react';
import { Tree } from 'react-organizational-chart'; // Removed TreeNode import
import './styles.css';

const Alert = ({ message }) => {
    return (
        <div className="alert">
            {message}
        </div>
    );
};

// Removed the Holon component as it's not needed anymore

const CommunicationPage = () => {
    const initialNodes = {
        id: 'root',
        name: 'Information System',
        children: [
            {
                id: 'Stakeholder',
                name: 'Stakeholder',
                children: [],
            },
            {
                id: 'Infrastructure',
                name: 'Infrastructure',
                children: [],
            }
        ],
    };

    const [selectedHolonIds, setSelectedHolonIds] = useState([]);
    const [hoveredHolonId, setHoveredHolonId] = useState(null);
    const [showAddSign, setShowAddSign] = useState(true);
    const [showRegulationLine, setShowRegulationLine] = useState(false);
    const [showAddMessage, setShowAddMessage] = useState(false);

    const toggleAddMessage = (value) => {
        setShowAddMessage(value);
    };

    const handleSelectNode = (node) => {
        setSelectedHolonIds([node.id]);
        setShowAddSign(true);
    };

    const isSelected = (node) => selectedHolonIds.includes(node.id);

    return (
        <div className="app-container">
            <div className="content-area">
                <Tree lineWidth={"2px"} lineColor={"blue"} lineBorderRadius={"5px"} lineStyle={"dashed"}>
                    {/* Removed the Holon component */}
                </Tree>
            </div>
            {showAddMessage && <Alert message="Click another holon to add an information flow." />}
        </div>
    );
};

export default CommunicationPage;
