import React, { useState } from 'react';
import ReactFlow, {
  ReactFlowProvider,
  addEdge,
  Controls,
} from 'react-flow-renderer';
import Button from 'react-bootstrap/Button';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlus } from '@fortawesome/free-solid-svg-icons';
import 'react-flow-renderer/dist/style.css';
import 'react-flow-renderer/dist/theme-default.css';
import './styles.css';

const Alert = ({ message }) => {
  return (
    <div className="alert">
      {message}
    </div>
  );
};

const initialElements = [
  {
    id: '1',
    type: 'special',
    data: { label: 'Information System' },
    position: { x: 250, y: 0 },
  },
  {
    id: '2',
    type: 'special',
    data: { label: 'Stakeholder' },
    position: { x: 100, y: 100 },
  },
  {
    id: '3',
    type: 'special',
    data: { label: 'Infrastructure' },
    position: { x: 400, y: 100 },
  },
  {
    id: 'e1-2',
    source: '1',
    target: '2',
    type: 'dashed',
  },
  {
    id: 'e1-3',
    source: '1',
    target: '3',
    type: 'dashed',
  },
];

const getNodeColor = (node, selectedNode, hoveredNode) => {
  if (node.id === selectedNode?.id) return '#00f'; // Blue for selected node
  if (node.id === hoveredNode?.id) return '#0f0'; // Green for hovered node
  return '#fff';
};

const CommunicationPage = () => {
  const [elements, setElements] = useState(initialElements);
  const [selectedNode, setSelectedNode] = useState(null);
  const [hoveredNode, setHoveredNode] = useState(null);
  const [isAddingFlow, setIsAddingFlow] = useState(false);

  const onConnect = (params) =>
    setElements((els) =>
      addEdge(
        { ...params, type: 'smoothstep', animated: true, style: { stroke: 'green' } },
        els
      )
    );

  const onNodeMouseEnter = (_event, node) => {
    if (isAddingFlow) {
      setHoveredNode(node);
    }
  };
  const onNodeMouseLeave = () => setHoveredNode(null);
  const onNodeClick = (_event, node) => {
    if (isAddingFlow) {
      if (node.id !== selectedNode.id) {
        const newEdge = {
          id: `e${selectedNode.id}-${node.id}`,
          source: selectedNode.id,
          target: node.id,
          animated: true,
          style: { stroke: 'green' },
        };
        setElements((els) => addEdge(newEdge, els));
        setIsAddingFlow(false);
        setSelectedNode(null);
      }
    } else {
      setSelectedNode(node);
      setIsAddingFlow(true);
    }
  };

  const nodeTypes = {
    special: (node) => (
      <div
        style={{ background: getNodeColor(node, selectedNode, hoveredNode) }}
        className="node"
      >
        {node.data.label}
      </div>
    ),
  };

  return (
    <div style={{ height: 600 }}>
      <ReactFlowProvider>
        <ReactFlow
          elements={elements}
          onConnect={onConnect}
          nodeTypes={nodeTypes}
          onNodeMouseEnter={onNodeMouseEnter}
          onNodeMouseLeave={onNodeMouseLeave}
          onNodeClick={onNodeClick}
          snapToGrid={true}
          snapGrid={[15, 15]}
        >
          <Controls />
        </ReactFlow>
        {isAddingFlow && <Alert message="Click another holon to add an information flow." />}
      </ReactFlowProvider>
    </div>
  );
};

export default CommunicationPage;
