import React, { useState } from 'react';
import './styles.css';

const Holon = ({ name, onSelect, onAdd, onRemove }) => {
  const stopPropagation = (e) => e.stopPropagation();

  return (
    <div className="holon" onClick={() => onSelect(name)}>
      <div className="name">{name}</div>
      <button className="add-button" onClick={(e) => { stopPropagation(e); onAdd(name); }}>+</button>
      <button className="remove-button" onClick={(e) => { stopPropagation(e); onRemove(name); }}>x</button>
    </div>
  );
};

const App = () => {
  const [nodes, setNodes] = useState([
    {
      id: 'root',
      name: 'Information System',
      description: 'An Information System is a set of components for collecting, storing, and processing data.',
      children: [],
    }
  ]);
  const [idCounter, setIdCounter] = useState(0);

  const selectHolon = (name) => {
    // Implement as needed, possibly to show details of the selected holon
  };

  const addChild = (parentName) => {
    const childName = prompt('Enter the name of the child:');
    if (!childName || childName.trim() === '') {
      alert('Please enter a valid name for the child.');
      return;
    }

    setNodes((prevNodes) => {
      const newId = `node-${++idCounter}`; // Increment ID counter and use for the new node
      const newNodes = JSON.parse(JSON.stringify(prevNodes)); // Deep copy to avoid direct state mutation

      // Recursive function to find the parent node and add the new child
      const addNodeRecursively = (nodes) => {
        nodes.forEach(node => {
          if (node.name === parentName) {
            node.children.push({ id: newId, name: childName, description: '', children: [] });
          } else {
            addNodeRecursively(node.children);
          }
        });
      };

      addNodeRecursively(newNodes);
      return newNodes;
    });
  };

  const removeChild = (nodeName) => {
    // Similar to addChild, recursively find and remove the node by name
  };

  // Render nodes recursively
  const renderNodes = (nodes) => nodes.map(node => (
    <div key={node.id}>
      <Holon name={node.name} onSelect={selectHolon} onAdd={addChild} onRemove={removeChild} />
      {node.children.length > 0 && <div className="children">{renderNodes(node.children)}</div>}
    </div>
  ));

  return (
    <div className="app-container">
      <div className="holons-container">
        {renderNodes(nodes)}
      </div>
      <div className="holon-details">
        {/* Details rendering */}
      </div>
    </div>
  );
};

export default App;
