import React from 'react';
import { Form, Button, Container, Row, Col, ProgressBar } from 'react-bootstrap';

const IdeaSubmissionForm = () => {
  return (
    <Container className="my-5 form-container shadow-lg rounded p-5">
      <h2 className="text-center mb-4 form-title">BugXTech Idea Submission Form</h2>
      <ProgressBar now={50} label={`50% Complete`} className="mb-4 progress-bar-custom" />
      <Form>
        <Row>
          <Col md={6}>
            {/* Name Field */}
            <Form.Group controlId="name">
              <Form.Label className="form-label">Name</Form.Label>
              <Form.Control type="text" placeholder="Enter your name" required className="form-input" />
            </Form.Group>
          </Col>

          <Col md={6}>
            {/* Email Address Field */}
            <Form.Group controlId="email">
              <Form.Label className="form-label">Email Address</Form.Label>
              <Form.Control type="email" placeholder="Enter your email address" required className="form-input" />
            </Form.Group>
          </Col>
        </Row>

        {/* Idea Title Field */}
        <Form.Group controlId="ideaTitle" className="mt-4">
          <Form.Label className="form-label">Idea Title</Form.Label>
          <Form.Control type="text" placeholder="Enter your idea title" required className="form-input" />
        </Form.Group>

        {/* Idea Description Field */}
        <Form.Group controlId="ideaDescription" className="mt-4">
          <Form.Label className="form-label">Idea Description</Form.Label>
          <Form.Control as="textarea" rows={3} placeholder="Describe your idea in detail" required className="form-textarea" />
        </Form.Group>

        {/* Target Audience Field */}
        <Form.Group controlId="targetAudience" className="mt-4">
          <Form.Label className="form-label">Target Audience</Form.Label>
          <Form.Control type="text" placeholder="Who are the participants for this idea?" required className="form-input" />
        </Form.Group>

        {/* Problem Statement Field */}
        <Form.Group controlId="problemStatement" className="mt-4">
          <Form.Label className="form-label">Problem Statement</Form.Label>
          <Form.Control as="textarea" rows={3} placeholder="What problem does this idea solve for the participants?" required className="form-textarea" />
        </Form.Group>

        {/* Unique Selling Proposition Field */}
        <Form.Group controlId="usp" className="mt-4">
          <Form.Label className="form-label">Unique Selling Proposition</Form.Label>
          <Form.Control as="textarea" rows={3} placeholder="What makes this idea unique for the participants?" required className="form-textarea" />
        </Form.Group>

        {/* Potential Impact Field */}
        <Form.Group controlId="potentialImpact" className="mt-4">
          <Form.Label className="form-label">Potential Impact</Form.Label>
          <Form.Control as="textarea" rows={3} placeholder="How will this idea impact the participants?" required className="form-textarea" />
        </Form.Group>

        {/* Collaboration Interest Field */}
        <Form.Group controlId="collaborationInterest" className="mt-4">
          <Form.Label className="form-label">Collaboration Interest</Form.Label>
          <Form.Control as="textarea" rows={3} placeholder="Are you interested in collaborating with others on this idea?" className="form-textarea" />
        </Form.Group>

        {/* LinkedIn Profile Links Field */}
        <Row>
          <Col md={6}>
            <Form.Group controlId="linkedin" className="mt-4">
              <Form.Label className="form-label">LinkedIn Profile Links (optional)</Form.Label>
              <Form.Control type="text" placeholder="Enter your LinkedIn profile link" className="form-input" />
            </Form.Group>
          </Col>
          <Col md={6}>
            {/* Hashtag Usage Field */}
            <Form.Group controlId="hashtagUsage" className="mt-4">
              <Form.Label className="form-label">Hashtag Usage</Form.Label>
              <Form.Control type="text" placeholder="Please include the competition hashtag" required className="form-input" />
            </Form.Group>
          </Col>
        </Row>

        {/* LinkedIn Share Checkbox */}
        <Form.Group controlId="linkedinShare" className="mt-4">
          <Form.Check 
            type="checkbox"
            label="I confirm I will like a post from BugXTech on LinkedIn and share it to my network, tagging BugXTech." 
            className="form-check"
          />
          <Form.Text className="text-muted small-text">(Optional: If you do not wish to share on LinkedIn, you may skip this step, though sharing is encouraged for broader engagement.)</Form.Text>
        </Form.Group>

        {/* Trust Factor */}
        <div className="mt-5">
          <p className="text-muted trust-text">
            <strong>Trust Factor:</strong> We value your privacy. Rest assured that your idea will not be shared with any third parties and will only be used internally for evaluation and collaboration.
          </p>
        </div>

        {/* Submit Button */}
        <Button variant="primary" type="submit" className="mt-4 submit-button">Submit Your Idea</Button>
      </Form>
    </Container>
  );
};

export default IdeaSubmissionForm;

/* Inline CSS */
const styles = `
  .form-container {
    background-color: #f9f9f9;
    border-radius: 10px;
    padding: 30px;
    max-width: 1200px;
    margin: auto;
  }

  .form-title {
    font-family: 'Roboto', sans-serif;
    font-weight: bold;
    color: #0d6efd;
  }

  .form-label {
    font-weight: bold;
    color: #343a40;
  }

  .form-input,
  .form-textarea {
    border-radius: 5px;
    border: 1px solid #ced4da;
    padding: 10px;
  }

  .form-input:focus,
  .form-textarea:focus {
    border-color: #0d6efd;
    box-shadow: 0 0 5px rgba(13, 110, 253, 0.5);
  }

  .form-check {
    margin-top: 10px;
  }

  .small-text {
    font-size: 0.9rem;
  }

  .trust-text {
    font-size: 0.9rem;
    line-height: 1.5;
  }

  .submit-button {
    background-color: #0d6efd;
    border: none;
    padding: 10px 20px;
    font-weight: bold;
    transition: background-color 0.3s;
  }

  .submit-button:hover {
    background-color: #0b5ed7;
  }

  .progress-bar-custom {
    height: 20px;
    border-radius: 5px;
  }
`;

const styleSheet = document.createElement("style");
styleSheet.type = "text/css";
styleSheet.innerText = styles;
document.head.appendChild(styleSheet);
