import React, { useState } from 'react';

// A single Holon component that can recursively render itself to represent children
const Holon = ({ holon, onSelect, onAdd, onRemove }) => {
  return (
    <div className="holon">
      <div className="holon-details" onClick={() => onSelect(holon)}>
        <div className="name">{holon.name}</div>
        <div className="buttons">
          <button onClick={() => onAdd(holon.id)}>Add Subordinate</button>
          {holon.id !== 'root' && <button onClick={() => onRemove(holon.id)}>Remove</button>}
        </div>
      </div>
      {holon.children.length > 0 && (
        <div className="children">
          {holon.children.map(child => (
            <Holon key={child.id} holon={child} onSelect={onSelect} onAdd={onAdd} onRemove={onRemove} />
          ))}
        </div>
      )}
    </div>
  );
};

