import React, { useCallback, useState } from "react";
import ReactFlow, {
  addEdge,
  useNodesState,
  useEdgesState,
  MarkerType,
} from "reactflow";
import Modal from "react-modal";
import "reactflow/dist/style.css";
import "bootstrap/dist/css/bootstrap.min.css";
import "./styles.css";
import CustomNodeFlow from "./CustomNodeFlow";

const nodeTypes = {
  customNode: CustomNodeFlow,
};

const fitViewOptions = { padding: 0.5 };

Modal.setAppElement("#root");

function CommunicationReactFlow(props) {
  const [nodes, setNodes, onNodesChange] = useNodesState(props.nodes);
  const [edges, setEdges, onEdgesChange] = useEdgesState(props.edges);
  const [sourceNode, setSourceNode] = useState(null);
  const [destinationNode, setDestinationNode] = useState();
  const [selectedNode, setSelectedNode] = useState(null);
  const [selectedInformationFlow, setSelectedInformationFlow] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [enableInformationFlow, setEnableInformationFlow] = useState(false);
  const [viewMode, setViewMode] = useState("all"); // "all" or "selected"
  const [informationFlows, setInformationFlows] = useState([]);

  const onConnect = useCallback(
    (params) => setEdges((eds) => addEdge(params, eds)),
    [setEdges]
  );

  const handleNodeClick = (event, nodeData) => {
    setSelectedNode(nodeData);
    setViewMode("selected");
    showSelectedHolonFlows(nodeData);
  };

  const showAllFlows = () => {
    setViewMode("all");
    setNodes((nds) =>
      nds.map((node) => ({
        ...node,
        style: { border: "2px solid blue" },
      }))
    );
    setEdges((eds) =>
      eds.map((edge) => {
        const isInFlow = informationFlows.some(
          (flow) =>
            (flow[0].id === edge.source && flow[1].id === edge.target) ||
            (flow[0].id === edge.target && flow[1].id === edge.source)
        );
        return {
          ...edge,
          animated: isInFlow,
          style: {
            stroke: isInFlow ? "green" : "black",
            strokeDasharray: isInFlow ? "5,5" : undefined,
          },
        };
      })
    );
  };

  const showSelectedHolonFlows = (selectedNode) => {
    if (selectedNode) {
      setNodes((nds) =>
        nds.map((node) => ({
          ...node,
          style: {
            border: node.id === selectedNode.id ? "2px solid lightblue" : "none",
          },
        }))
      );
      setEdges((eds) =>
        eds.map((edge) => {
          const isConnected =
            (edge.source === selectedNode.id || edge.target === selectedNode.id) &&
            informationFlows.some(
              (flow) =>
                (flow[0].id === edge.source && flow[1].id === edge.target) ||
                (flow[0].id === edge.target && flow[1].id === edge.source)
            );
          return {
            ...edge,
            animated: isConnected,
            style: {
              stroke: isConnected ? "green" : "black",
              strokeDasharray: isConnected ? "5,5" : undefined,
            },
          };
        })
      );
    }
  };

  const handleAddInformationFlow = () => {
    setEnableInformationFlow(!enableInformationFlow);
    if (enableInformationFlow) {
      setSourceNode(null);
      setDestinationNode(null);
      setNodes((nds) => nds.map((n) => ({ ...n, style: { border: "none" } })));
      setEdges((eds) =>
        eds.map((edge) => ({
          ...edge,
          animated: false,
          style: { stroke: "black" },
        }))
      );
    }
  };

  const handleNodeSelectionForFlow = (event, nodeData) => {
    const id = nodeData.id;
    const node = nodes.find((n) => n.id === id);

    if (enableInformationFlow) {
      if (!sourceNode) {
        setSourceNode(node);
        setNodes((nds) =>
          nds.map((n) =>
            n.id === node.id ? { ...n, style: { border: "2px solid blue" } } : n
          )
        );
      } else if (sourceNode.id !== id) {
        setDestinationNode(node);

        // Only add the flow if it doesn't already exist
        if (!informationFlows.some((flow) => flow[0].id === sourceNode.id && flow[1].id === node.id)) {
          setInformationFlows([...informationFlows, [sourceNode, node]]);
        }
        setNodes((nds) =>
          nds.map((n) =>
            n.id === node.id
              ? { ...n, style: { border: "2px solid green" } }
              : n
          )
        );
        setTimeout(handleAddInformationFlow, 1000);
      }
    }
  };

  return (
    <div
      className="container-fluid"
      style={{ display: "flex", height: "100vh", width: "100vw" }}
    >
      <div className="col-9" style={{ height: "100%" }}>
        <div
          className="d-flex justify-content-between align-items-center mb-3"
          style={{ position: "absolute", top: -88, right: "-23vw" }}
        >
          <button
            className={"btn btn-success"}
            onClick={handleAddInformationFlow}
          >
            Add Information Flow
          </button>
          <button
            className={"btn btn-primary ml-2"}
            onClick={showAllFlows}
            style={{
              backgroundColor: viewMode === "all" ? "blue" : "grey",
            }}
          >
            All
          </button>
          <button
            className={"btn btn-primary"}
            onClick={() => showSelectedHolonFlows(selectedNode)}
            style={{
              backgroundColor: viewMode === "selected" ? "lightblue" : "grey",
            }}
            disabled={!selectedNode}
          >
            Selected Holon
          </button>
        </div>

        <ReactFlow
          nodeTypes={nodeTypes}
          nodes={nodes}
          edges={edges}
          onNodesChange={onNodesChange}
          onEdgesChange={onEdgesChange}
          onConnect={onConnect}
          fitView
          fitViewOptions={fitViewOptions}
          style={{ width: "100%", height: "90%" }}
          onNodeClick={enableInformationFlow ? handleNodeSelectionForFlow : handleNodeClick}
          onPaneClick={() => setSelectedNode(null)}
        />
      </div>
      <div
        className="col-3"
        style={{ padding: "10px", borderLeft: "1px solid #ccc" }}
      >
        {selectedNode ? (
          <div className="mr-3">
            <h3>Holon Details</h3>
            <div className="form-group mb-3">
              <label htmlFor="nameofNode">Name</label>
              <input
                type="text"
                id="nameofNode"
                className="form-control"
                value={selectedNode.data.label}
                readOnly
              />
            </div>
          </div>
        ) : (
          <div>No Selection</div>
        )}
      </div>
      <Modal isOpen={isModalOpen} onRequestClose={() => setIsModalOpen(false)}>
        {/* Modal Content */}
      </Modal>
    </div>
  );
}

export default CommunicationReactFlow;
