import React, { useState } from 'react';
import { Tree, TreeNode } from 'react-organizational-chart';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlus, faTrash } from '@fortawesome/free-solid-svg-icons';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Modal, Button, Form } from 'react-bootstrap';

const Holon = ({ node, onAddChild, onRemoveNode, onSelectNode, isSelected }) => {
    const [showRadio, setShowRadio] = useState(false);
    const [radioValue, setRadioValue] = useState("child");

    const handleActionClick = (e, action) => {
        e.stopPropagation();
        action();
    };

    const selected = isSelected(node);

    const handleAddClick = () => {
        setShowRadio(true);
    };

    const handleRadioChange = (e) => {
        setRadioValue(e.target.value);
    };

    const handleRadioSubmit = () => {
        onAddChild(node, radioValue === "parent");
        setShowRadio(false);
    };

    return (
        <TreeNode label={
            <div className={`tree-label ${selected ? 'selected' : ''}`} onClick={() => onSelectNode(node)}>
                <div className="name">{node.name}</div>
                <div className="buttons">
                    <Button variant="primary" size="sm" className="me-1" onClick={(e) => handleActionClick(e, handleAddClick)}>
                        <FontAwesomeIcon icon={faPlus} />
                    </Button>
                    <Button variant="danger" size="sm" onClick={(e) => handleActionClick(e, () => onRemoveNode(node.id))}>
                        <FontAwesomeIcon icon={faTrash} />
                    </Button>
                </div>
            </div>
        }>
            {showRadio && (
                <Form onSubmit={handleRadioSubmit}>
                    <Form.Check 
                        type="radio"
                        label="Child Node"
                        value="child"
                        checked={radioValue === "child"}
                        onChange={handleRadioChange}
                    />
                    {node.children.length === 0 && (
                        <Form.Check 
                            type="radio"
                            label="Parent Node"
                            value="parent"
                            checked={radioValue === "parent"}
                            onChange={handleRadioChange}
                        />
                    )}
                    <Button variant="primary" size="sm" type="submit">Submit</Button>
                </Form>
            )}
            {node.children && node.children.map(child => (
                <Holon
                    key={child.id}
                    node={child}
                    onAddChild={onAddChild}
                    onRemoveNode={onRemoveNode}
                    onSelectNode={onSelectNode}
                    isSelected={isSelected}
                />
            ))}
        </TreeNode>
    );
};

const StructurePage = ({ nodes, onAddChild, onRemoveNode, onSelectNode, isSelected }) => {
    return (
        <div className="page">
            <div className="tree-container">
                <Tree lineWidth={"2px"} lineColor={"blue"} lineBorderRadius={"5px"} lineStyle={"dashed"}>
                    <Holon
                        node={nodes}
                        onAddChild={onAddChild}
                        onRemoveNode={onRemoveNode}
                        onSelectNode={onSelectNode}
                        isSelected={(node) => isSelected && node.id === isSelected.id}
                    />
                </Tree>
            </div>
        </div>
    );
};

export default StructurePage;
