import React, { useState } from 'react';
import './styles.css';

const Holon = ({ node, onSelect, onAdd, onRemove, level = 0 }) => {
  const addButton = level > 0 ? (
    <button className="add-sibling-button" onClick={(e) => { e.stopPropagation(); onAdd(node, true); }}>Add Sibling</button>
  ) : null;

  return (
    <div className="holon">
      <div className="holon-details" onClick={() => onSelect(node)}>
        <div className="name">{node.name}</div>
        <div className="buttons">
          <button className="add-button" onClick={(e) => { e.stopPropagation(); onAdd(node, false); }}>+</button>
          {addButton}
          <button className="remove-button" onClick={(e) => { e.stopPropagation(); onRemove(node.id); }}>x</button>
        </div>
      </div>
      {node.children && node.children.length > 0 && (
        <div className="children">
          {node.children.map(child => (
            <Holon key={child.id} node={child} onSelect={onSelect} onAdd={onAdd} onRemove={onRemove} level={level + 1} />
          ))}
        </div>
      )}
    </div>
  );
};

const App = () => {
  const [nodes, setNodes] = useState([{
    id: 'root',
    name: 'Information System',
    description: 'An Information System is a set of components for collecting, storing, and processing data.',
    children: [
      {
        id: 'stakeholder',
        name: 'Stakeholder',
        description: 'A stakeholder refers to any individual, group, or organization that has an interest or concern in the system, its development, implementation, or outcomes. These stakeholders can be internal or external to the organization implementing the information system. Understanding and managing stakeholders is crucial for the success of an information system project.',
        children: [],
      },
      {
        id: 'infrastructure',
        name: 'Infrastructure',
        description: 'Infrastructure is the foundation or framework that supports a system or organization.',
        children: [],
      }
    ],
  }]);
  const [selectedHolon, setSelectedHolon] = useState(null);
  const [idCounter, setIdCounter] = useState(3);

  const selectHolon = (node) => {
    setSelectedHolon(node);
  };

  const addChild = (node, isSibling) => {
    const childName = prompt('Enter the name of the new node:');
    if (!childName) return;
    const newId = `node-${idCounter}`;
    const newNode = {
      id: newId,
      name: childName,
      description: '',
      children: [],
    };

    setIdCounter(idCounter + 1);

    if (isSibling) {
      // Find parent of the current node and add the new node as a sibling
      const addSibling = (nodes, parentId, newNode) => {
        return nodes.map((node) => {
          if (node.id === parentId) {
            return { ...node, children: [...node.children, newNode] };
          } else if (node.children) {
            return { ...node, children: addSibling(node.children, parentId, newNode) };
          }
          return node;
        });
      };
      setNodes(prevNodes => addSibling(prevNodes, node.id, newNode));
    } else {
      // Add the new node as a child
      const addChildRecursively = (nodes, nodeId, newNode) => {
        return nodes.map((node) => {
          if (node.id === nodeId) {
            return { ...node, children: [...node.children, newNode] };
          } else if (node.children) {
            return { ...node, children: addChildRecursively(node.children, nodeId, newNode) };
          }
          return node;
        });
      };
      setNodes(prevNodes => addChildRecursively(prevNodes, node.id, newNode));
    }
  };

  const removeChild = (nodeId) => {
    const removeNodeRecursively = (nodes, nodeId) => {
      return nodes.reduce((acc, node) => {
        if (node.id !== nodeId) {
          if (node.children) {
            const filteredChildren = removeNodeRecursively(node.children, nodeId);
            acc.push({ ...node, children: filteredChildren });
          } else {
            acc.push(node);
          }
        }
        return acc;
      }, []);
    };
    setNodes(prevNodes => removeNodeRecursively(prevNodes, nodeId));
  };

  return (
    <div className="app-container">
      <div className="holons-container">
        {nodes.map(node => (
          <Holon key={node.id} node={node} onSelect={selectHolon} onAdd={addChild} onRemove={removeChild} />
        ))}
      </div>
      <div className="holon-details">
        {selectedHolon ? (
          <>
            <h2>Holon Details</h2>
            <p>Name: {selectedHolon.name}</p>
            <p>Description: {selectedHolon.description || 'No description provided.'}</p>
          </>
        ) : (
          <p>Select a holon to see details.</p>
        )}
      </div>
    </div>
  );
};

export default App;
