import React, { useState } from 'react';
import { Tree, TreeNode } from 'react-organizational-chart';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlus, faTrash } from '@fortawesome/free-solid-svg-icons';
import 'bootstrap/dist/css/bootstrap.min.css';
import Button from 'react-bootstrap/Button';
import './styles.css';

const Holon = ({ node, onAddChild, onRemoveNode, onSelectNode, isSelected, isRoot, addNewRoot }) => {
    const handleActionClick = (e, action) => {
        e.stopPropagation();
        action();
    };

    const selected = isSelected(node);

    return (
        <TreeNode label={
            <div className={`p-2 mb-2 border ${selected ? 'tree-label selected' : 'tree-label'} rounded`} onClick={() => onSelectNode(node)}>
                <div>{node.name}</div>
                <div className="d-flex justify-content-end">
                    <Button variant="primary" size="sm" className="me-1" onClick={(e) => handleActionClick(e, () => onAddChild(node, true))}>
                        <FontAwesomeIcon icon={faPlus} />
                    </Button>
                    <Button variant="danger" size="sm" onClick={(e) => handleActionClick(e, () => onRemoveNode(node.id))}>
                        <FontAwesomeIcon icon={faTrash} />
                    </Button>
                </div>
            </div>
        }>
            {node.children && node.children.map(child => (
                <Holon
                    key={child.id}
                    node={child}
                    onAddChild={onAddChild}
                    onRemoveNode={onRemoveNode}
                    onSelectNode={onSelectNode}
                    isSelected={isSelected}
                    addNewRoot={addNewRoot}
                />
            ))}
        </TreeNode>
    );
};

const StructurePage = ({ nodes, onAddChild, onRemoveNode, onSelectNode, isSelected, addNewRoot }) => {
    return (
        <div>
            <div className="steps-bar">
                <div className="step active">
                    <div className="step-circle">1</div>
                    <div className="step-label">Step 1</div>
                </div>
                <div className="step">
                    <div className="step-circle">2</div>
                    <div className="step-label">Step 2</div>
                </div>
            </div>
            <Tree lineWidth={"2px"} lineColor={"blue"} lineBorderRadius={"5px"} lineStyle={"dashed"}>
                <Holon
                    node={nodes}
                    onAddChild={onAddChild}
                    onRemoveNode={onRemoveNode}
                    onSelectNode={onSelectNode}
                    isSelected={(node) => isSelected && node.id === isSelected.id}
                    addNewRoot={addNewRoot}
                />
            </Tree>
        </div>
    );
};

const App = () => {
    const [nodes, setNodes] = useState({
        id: 1,
        name: 'Root Node',
        children: [],
    });
    const [selectedNode, setSelectedNode] = useState(null);

    const handleAddChild = (parentNode, isChild) => {
        const newNode = {
            id: Date.now(),
            name: isChild ? 'New Child Node' : 'New Parent Node',
            children: [],
        };

        const addNodeRecursively = (node) => {
            if (node.id === parentNode.id) {
                node.children.push(newNode);
            } else {
                node.children.forEach(child => addNodeRecursively(child));
            }
        };

        const newNodes = { ...nodes };
        addNodeRecursively(newNodes);
        setNodes(newNodes);
    };

    const handleRemoveNode = (nodeId) => {
        const removeNodeRecursively = (node) => {
            node.children = node.children.filter(child => child.id !== nodeId);
            node.children.forEach(removeNodeRecursively);
        };

        const newNodes = { ...nodes };
        removeNodeRecursively(newNodes);
        setNodes(newNodes);
    };

    const handleSelectNode = (node) => {
        setSelectedNode(node);
    };

    const addNewRoot = () => {
        const newRoot = {
            id: Date.now(),
            name: 'New Root Node',
            children: [nodes],
        };
        setNodes(newRoot);
    };

    return (
        <StructurePage
            nodes={nodes}
            onAddChild={handleAddChild}
            onRemoveNode={handleRemoveNode}
            onSelectNode={handleSelectNode}
            isSelected={selectedNode}
            addNewRoot={addNewRoot}
        />
    );
};

export default App;
