import React, { useState } from 'react';
import { Tree, TreeNode } from 'react-organizational-chart';
import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import './styles.css';

const Holon = ({ node, onAddChild, onRemoveNode, onSelectNode, isSelected }) => {
  const handleActionClick = (e, action) => {
    e.stopPropagation();
    action();
  };

  const selected = isSelected(node);

  return (
    <TreeNode label={
      <div className={`tree-label ${selected ? 'selected' : ''}`} onClick={() => onSelectNode(node)}>
        <div className="name">{node.name}</div>
        <div className="buttons">
          <button className="add-button" onClick={(e) => handleActionClick(e, () => onAddChild(node))}>+</button>
          {node.id !== 'root' && (
            <button className="remove-button" onClick={(e) => handleActionClick(e, () => onRemoveNode(node.id))}>x</button>
          )}
        </div>
      </div>
    }>
      {node.children && node.children.map(child => (
        <Holon 
          key={child.id} 
          node={child} 
          onAddChild={onAddChild} 
          onRemoveNode={onRemoveNode} 
          onSelectNode={onSelectNode} 
          isSelected={isSelected}
        />
      ))}
    </TreeNode>
  );
};

const App = () => {
  const initialNodes = {
    id: 'root',
    name: 'Information System',
    children: [
      {
        id: 'Stakeholder',
        name: 'Stakeholder',
        children: [],
      },
      {
        id: 'Infrastructure',
        name: 'Infrastructure',
        children: [],  
      }
    ],
  };

  const [nodes, setNodes] = useState(initialNodes);
  const [selectedHolon, setSelectedHolon] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [nodeName, setNodeName] = useState("");
  const [relationshipType, setRelationshipType] = useState("");

  const handleModalClose = () => setShowModal(false);
  const handleModalShow = () => setShowModal(true);

  const onAddChild = (parentNode) => {
    setRelationshipType('child');
    setSelectedHolon(parentNode);
    handleModalShow();
  };

  const addNewRoot = (parentNode) => {
    setRelationshipType('parent');
    setSelectedHolon(parentNode);
    handleModalShow();
  };

  const onRemoveNode = (nodeId) => {
    const removeNode = (nodes, nodeIdToRemove) => {
      if (nodes.id === nodeIdToRemove) {
        return null; // Can't remove root
      }
      nodes.children = nodes.children.filter(child => child.id !== nodeIdToRemove);
      nodes.children.forEach(child => removeNode(child, nodeIdToRemove));
      return nodes;
    };

    const newNodes = removeNode({ ...nodes }, nodeId);
    setNodes(newNodes);
    setSelectedHolon(null);
  };

  const isSelected = (node) => selectedHolon && node.id === selectedHolon.id;

  const onSelectNode = (node) => {
    setSelectedHolon(node);
  };

  const handleSubmit = () => {
    if (!nodeName) return;

    if (relationshipType === "child") {
      const newChild = {
        id: `${selectedHolon.id}-${Math.random()}`,
        name: nodeName,
        children: []
      };

      const addNode = (nodes, parentId) => {
        if (nodes.id === parentId) {
          nodes.children.push(newChild);
          return;
        }

        nodes.children.forEach(child => addNode(child, parentId));
      };

      const newNodes = { ...nodes };
      addNode(newNodes, selectedHolon.id);
      setNodes(newNodes);
    } else if (relationshipType === "parent") {
      const newParent = {
        id: `root-${Math.random()}`,
        name: nodeName,
        children: [selectedHolon]
      };

      setNodes(newParent);
    }

    setNodeName("");
    handleModalClose();
  };

  return (
    <div className="app-container">
      <div className="content-area">
        <Tree lineWidth={"2px"} lineColor={"blue"} lineBorderRadius={"5px"}>
          <Holon
            node={nodes}
            onAddChild={onAddChild}
            onRemoveNode={onRemoveNode}
            onSelectNode={onSelectNode}
            isSelected={isSelected}
          />
        </Tree>
        <Modal show={showModal} onHide={handleModalClose}>
          <Modal.Header closeButton>
            <Modal.Title>Enter Details</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <Form>
              <Form.Group controlId="formNodeName">
                <Form.Label>{relationshipType === 'child' ? 'Enter "child" node name' : 'Enter "parent" node name'}</Form.Label>
                <Form.Control type="text" placeholder="Node name" value={nodeName} onChange={e => setNodeName(e.target.value)} />
              </Form.Group>
            </Form>
          </Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={handleModalClose}>
              Close
            </Button>
            <Button variant="primary" onClick={handleSubmit}>
              Save Changes
            </Button>
          </Modal.Footer>
        </Modal>
      </div>
    </div>
  );
};

export default App;
