import React, { useState } from 'react';
import './styles.css'; // Import the CSS file containing styles

const Holon = ({ name, onAdd, onRemove, onSelect }) => {
  return (
    <div className="holon" onClick={() => onSelect(name)}>
      <div className="name">{name}</div>
      <button className="add-button" onClick={(e) => { e.stopPropagation(); onAdd(); }}>+</button>
      <button className="remove-button" onClick={(e) => { e.stopPropagation(); onRemove(); }}>x</button>
    </div>
  );
};

const App = () => {
  const [nodes, setNodes] = useState([
    {
      id: 'stakeholder',
      name: 'Stakeholder',
      description: 'A stakeholder is an individual, group, or organization that has interest or concern in an organization.',
      children: [],
    },
    {
      id: 'root',
      name: 'Information System',
      description: 'An Information System is a set of components for collecting, storing, and processing data.',
      children: [],
    },
    {
      id: 'infrastructure',
      name: 'Infrastructure',
      description: 'Infrastructure in an IT context is the physical hardware used to interconnect computers and users.',
      children: [],
    }
  ]);
  const [selectedHolon, setSelectedHolon] = useState(null);

  const addChild = (parentId, childName) => {
    // Implementation remains the same
  };

  const removeChild = (parentId, childId) => {
    // Implementation remains the same
  };

  const selectHolon = (name) => {
    const holon = nodes.find(node => node.name === name);
    setSelectedHolon(holon);
  };

  return (
    <div className="app-container">
      <div className="holon-container">
        {nodes.map(node => (
          <Holon
            key={node.id}
            name={node.name}
            onAdd={() => addChild(node.id, "New Child")}
            onRemove={() => removeChild(node.id, node.id)}
            onSelect={selectHolon}
          />
        ))}
      </div>
      <div className="holon-details">
        {selectedHolon ? (
          <>
            <h2>Holon Details</h2>
            <p>Name: {selectedHolon.name}</p>
            <p>Description: {selectedHolon.description}</p>
          </>
        ) : (
          <p>Select a holon to see details</p>
        )}
      </div>
    </div>
  );
};

export default App;
