import React, { useState } from 'react';
import './styles.css';

const Holon = ({ name, onSelect, onAdd, onRemove }) => {
  return (
    <div className="holon" onClick={() => onSelect(name)}>
      <div className="name">{name}</div>
      <button className="add-button" onClick={(e) => onAdd(e, name)}>+</button>
      <button className="remove-button" onClick={(e) => onRemove(e, name)}>x</button>
    </div>
  );
};

const App = () => {
  const [nodes, setNodes] = useState([
    {
      id: 'root',
      name: 'Information System',
      description: 'An Information System is a set of components for collecting, storing, and processing data.',
      children: [],
    }
  ]);
  const [selectedHolon, setSelectedHolon] = useState(null);
  const [idCounter, setIdCounter] = useState(1); // Keep track of IDs to ensure uniqueness

  const selectHolon = (name) => {
    // Function remains the same
  };

  const addChild = (e, parentName) => {
    e.stopPropagation(); // Prevent triggering onSelect when adding
    const childName = prompt('Enter the name of the child:');
    if (!childName || childName.trim() === '') {
      alert('Please enter a valid name for the child.');
      return;
    }

    const uniqueId = `node-${idCounter}`; // Ensure each node has a unique ID
    setIdCounter(idCounter + 1); // Increment the ID counter for the next node

    setNodes((currentNodes) => {
      const addNodeRecursively = (nodes, parentName) => {
        return nodes.map((node) => {
          if (node.name === parentName) {
            if (node.children.some(child => child.name === childName)) {
              alert('A node with this name already exists.');
              return node; // Return early if child name is not unique
            }
            const newNode = {
              id: uniqueId,
              name: childName,
              description: '', // Add a description if needed
              children: []
            };
            return { ...node, children: [...node.children, newNode] };
          }
          if (node.children.length > 0) {
            return { ...node, children: addNodeRecursively(node.children, parentName) };
          }
          return node;
        });
      };
      return addNodeRecursively(currentNodes, parentName);
    });
  };

  const removeChild = (e, nodeName) => {
    e.stopPropagation(); // Prevent onSelect from being triggered
    setNodes(currentNodes => removeNodeRecursively(currentNodes, nodeName));
  };

  // findNode and removeNodeRecursively functions remain the same

  return (
    <div className="app-container">
      <div className="holons-container">
        {nodes.map(node => (
          <div key={node.id}>
            <Holon
              name={node.name}
              onSelect={selectHolon}
              onAdd={addChild}
              onRemove={removeChild}
            />
            {node.children.length > 0 && (
              <div className="children">
                {node.children.map(child => (
                  <Holon
                    key={child.id}
                    name={child.name}
                    onSelect={selectHolon}
                    onAdd={(e) => addChild(e, child.name)}
                    onRemove={(e) => removeChild(e, child.name)}
                  />
                ))}
              </div>
            )}
          </div>
        ))}
      </div>
      <div className="holon-details">
        {selectedHolon ? (
          <>
            <h2>Holon Details</h2>
            <p>Name: {selectedHolon.name}</p>
            <p>Description: {selectedHolon.description}</p>
          </>
        ) : (
          <p>Select a holon to see details.</p>
        )}
      </div>
    </div>
  );
};

export default App;
