import React, { useState, useEffect } from 'react';
import { Tree, TreeNode } from 'react-organizational-chart';
import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlus, faTrash } from '@fortawesome/free-solid-svg-icons';
import './styles.css';

const Holon = ({ node, onAddChild, onRemoveNode, onSelectNode, isSelected, isHighlighted, onAddFlowStart, onAddFlowEnd }) => {
    const handleActionClick = (e, action) => {
        e.stopPropagation();
        action();
    };

    const selected = isSelected(node);
    const highlighted = isHighlighted(node);

    return (
        <TreeNode label={
            <div className={`tree-label ${selected ? 'selected' : ''} ${highlighted ? 'highlighted' : ''}`} onClick={() => onSelectNode(node)}>
                <div className="name">{node.name}</div>
                <div className="buttons">
                    <button className="add-button" onClick={(e) => handleActionClick(e, () => onAddChild(node))}>
                        <FontAwesomeIcon icon={faPlus} />
                    </button>
                    <button className="remove-button" onClick={(e) => handleActionClick(e, () => onRemoveNode(node.id))}>
                        <FontAwesomeIcon icon={faTrash} />
                    </button>
                </div>
                <button className="add-flow-button" onClick={(e) => handleActionClick(e, () => onAddFlowStart(node))}>
                    Add Flow
                </button>
            </div>
        }>
            {node.children && node.children.map(child => (
                <Holon
                    key={child.id}
                    node={child}
                    onAddChild={onAddChild}
                    onRemoveNode={onRemoveNode}
                    onSelectNode={onSelectNode}
                    isSelected={isSelected}
                    isHighlighted={isHighlighted}
                    onAddFlowStart={onAddFlowStart}
                    onAddFlowEnd={onAddFlowEnd}
                />
            ))}
        </TreeNode>
    );
};

const App = () => {
    const [nodes, setNodes] = useState(initialNodes);
    const [selectedHolon, setSelectedHolon] = useState(null);
    const [flowStart, setFlowStart] = useState(null);
    const [flowEnd, setFlowEnd] = useState(null);
    const [flows, setFlows] = useState([]);

    useEffect(() => {
        if (flowStart && flowEnd) {
            setFlows([...flows, { from: flowStart.id, to: flowEnd.id }]);
            setFlowStart(null);
            setFlowEnd(null);
        }
    }, [flowStart, flowEnd, flows]);

    const onAddFlowStart = (node) => {
        setFlowStart(node);
    };

    const onAddFlowEnd = (node) => {
        if (flowStart && node.id !== flowStart.id) {
            setFlowEnd(node);
        }
    };

    const isSelected = (node) => selectedHolon && node.id === selectedHolon.id;
    const isHighlighted = (node) => (flowStart && node.id === flowStart.id) || (flowEnd && node.id === flowEnd.id);

    return (
        <div className="app-container">
            <div className="steps-bar">
                {/* Steps bar code */}
            </div>
            <div className="content-area">
                <Tree lineWidth={"2px"} lineColor={"blue"} lineBorderRadius={"5px"}>
                    <Holon
                        node={nodes}
                        onAddChild={onAddChild}
                        onRemoveNode={onRemoveNode}
                        onSelectNode={(node) => setSelectedHolon(node)}
                        isSelected={isSelected}
                        isHighlighted={isHighlighted}
                        onAddFlowStart={onAddFlowStart}
                        onAddFlowEnd={onAddFlowEnd}
                    />
                </Tree>
                <div className="holon-details">
                    {/* Holon details code */}
                </div>
            </div>
        </div>
    );
};

export default App;
