import React, { useState } from 'react';
import './styles.css'; // Import the CSS file containing styles

const Holon = ({ name, onAdd, onRemove }) => {
  const [childName, setChildName] = useState('');

  const handleAddChild = () => {
    if (childName.trim() === '') {
      alert('Please enter a valid name for the child.');
      return;
    }

    onAdd(childName);
    setChildName('');
  };

  return (
    <div className="holon">
      <div className="name">{name}</div>
      <button className="add-button" onClick={handleAddChild}>+</button>
      <button className="remove-button" onClick={onRemove}>x</button>
    </div>
  );
};

const App = () => {
  const [nodes, setNodes] = useState([
    {
      id: 'stakeholder',
      name: 'Stakeholder',
      children: [],
    },
    {
      id: 'root',
      name: 'Information System',
      children: [],
    },
    {
      id: 'infrastructure',
      name: 'Infrastructure',
      children: [],
    }
  ]);

  const addChild = (parentId, childName) => {
    setNodes(prevNodes => {
      const parentIndex = prevNodes.findIndex(node => node.id === parentId);
      if (parentIndex !== -1) {
        if (prevNodes[parentIndex].children.some(child => child.name === childName)) {
          alert('This node already exists.');
          return prevNodes;
        }
        const newChild = { id: childName.toLowerCase().replace(/\s/g, '-'), name: childName, children: [] };
        return [
          ...prevNodes.slice(0, parentIndex),
          {
            ...prevNodes[parentIndex],
            children: [...prevNodes[parentIndex].children, newChild]
          },
          ...prevNodes.slice(parentIndex + 1)
        ];
      }
      return prevNodes;
    });
  };

  const removeChild = (parentId, childId) => {
    setNodes(prevNodes => {
      const parentIndex = prevNodes.findIndex(node => node.id === parentId);
      if (parentIndex !== -1) {
        const childIndex = prevNodes[parentIndex].children.findIndex(child => child.id === childId);
        if (childIndex !== -1) {
          return [
            ...prevNodes.slice(0, parentIndex),
            {
              ...prevNodes[parentIndex],
              children: [
                ...prevNodes[parentIndex].children.slice(0, childIndex),
                ...prevNodes[parentIndex].children.slice(childIndex + 1)
              ]
            },
            ...prevNodes.slice(parentIndex + 1)
          ];
        }
      }
      return prevNodes;
    });
  };

  return (
    <div className="container">
      <div className="stakeholder-container">
        {nodes.map(node => (
          <Holon
            key={node.id}
            name={node.name}
            onAdd={(childName) => addChild(node.id, childName)}
            onRemove={() => removeChild(node.id)}
          />
        ))}
      </div>
    </div>
  );
};

export default App;
