  id: 'root',
  name: 'Information System',
  description: 'The top-level information system',
  children: [
    {
      id: 'stakeholder',
      name: 'Stakeholder',
      description: 'Details about Stakeholder',
      children: [],
    },
    {
      id: 'infrastructure',
      name: 'Infrastructure',
      description: 'Details about Infrastructure',
      children: [],
    },
  ],
};

function App() {
  const [system, setSystem] = useState(initialSystem);
  const [selectedHolon, setSelectedHolon] = useState(null);

