// App.js
import React, { useState, useEffect } from 'react';
import './App.css';

const fetchHolonDescription = async (name) => {
  // Simulated API call
  // Replace this logic with your actual API request, e.g., using fetch or axios
  return new Promise((resolve) => {
    setTimeout(() => {
      resolve(`Description for ${name}`);
    }, 1000); // Simulate network delay
  });
};

const Holon = ({ node, onAddChild, onRemoveNode, onSelectNode, isSelected }) => {
  const handleActionClick = (e, action) => {
    e.stopPropagation();
    action();
  };

  return (
    <div
      className={`holon ${isSelected ? 'selected' : ''}`}
      onClick={() => onSelectNode(node)}
    >
      <div className="name">{node.name}</div>
      <div className="buttons">
        <button onClick={(e) => handleActionClick(e, () => onAddChild(node))}>
          +
        </button>
        {node.id !== 'root' && (
          <button onClick={(e) => handleActionClick(e, () => onRemoveNode(node.id))}>
            x
          </button>
        )}
      </div>
    </div>
  );
};

const App = () => {
  const [nodes, setNodes] = useState({
    id: 'root',
    name: 'Information System',
    children: [],
    description: 'An Information System manages information through collection, processing, and distribution.'
  });
  const [selectedHolon, setSelectedHolon] = useState(null);

  useEffect(() => {
    if (!selectedHolon) return;
    const fetchDescription = async () => {
      const description = await fetchHolonDescription(selectedHolon.name);
      setSelectedHolon((prev) => ({ ...prev, description }));
    };
    fetchDescription();
  }, [selectedHolon]);

  const onAddChild = (parentNode) => {
    const childName = prompt('Enter the name of the child:');
    if (!childName) return;
    const newChild = {
      id: `${parentNode.id}-${Math.random()}`, // This is a simplistic approach for generating IDs
      name: childName,
      children: [],
      description: `Description for ${childName}` // Placeholder description
    };
    const newNodes = JSON.parse(JSON.stringify(nodes)); // Deep clone nodes to avoid direct state mutation
    const addNodeRecursive = (node) => {
      if (node.id === parentNode.id) {
        node.children.push(newChild);
        return;
      }
      node.children.forEach(addNodeRecursive);
    };
    addNodeRecursive(newNodes);
    setNodes(newNodes);
  };

  const onRemoveNode = (nodeId) => {
    const newNodes = JSON.parse(JSON.stringify(nodes)); // Deep clone nodes
    const removeNodeRecursive = (node, parentId = null) => {
      if (node.id === nodeId) {
        return true; // Mark for deletion
      }
      node.children = node.children.filter((child) => !removeNodeRecursive(child, node.id));
      return false; // Do not mark parent nodes for deletion
    };
    removeNodeRecursive(newNodes);
    setNodes(newNodes);
    if (selectedHolon && selectedHolon.id === nodeId) {
      setSelectedHolon(null); // Deselect if the selected node is removed
    }
  };

  const onSelectNode = (node) => {
    setSelectedHolon(node);
  };

  return (
    <div className="app-container">
      <div className="holon-container">
        <Holon
          node={nodes}
          onAddChild={onAddChild}
          onRemoveNode={onRemoveNode}
          onSelectNode={onSelectNode}
          isSelected={(node) => selectedHolon && node.id === selectedHolon.id}
        />
      </div>
      {selectedHolon && (
        <div className="holon-details">
          <h2>Holon Details</h2>
          <p>Name: {selectedHolon.name}</p>
          <p>Description: {selectedHolon.description}</p>
        </div>
      )}
    </div>
  );
};

export default App;
