import React, { useState } from 'react';
import './App.css';

const Holon = ({ node, onSelect, onAdd, onRemove }) => {
  return (
    <div className="holon" onClick={() => onSelect(node)}>
      <div className="holon-name">{node.name}</div>
      <div className="holon-buttons">
        <button onClick={(e) => { e.stopPropagation(); onAdd(node.id); }}>+</button>
        <button onClick={(e) => { e.stopPropagation(); onRemove(node.id); }}>x</button>
      </div>
      {node.children && node.children.length > 0 && (
        <div className="holon-children">
          {node.children.map((child) => (
            <Holon key={child.id} node={child} onSelect={onSelect} onAdd={onAdd} onRemove={onRemove} />
          ))}
        </div>
      )}
    </div>
  );
};

const App = () => {
  const [nodes, setNodes] = useState([
    {
      id: 'root',
      name: 'Information System',
      description: 'An Information System is a set of components for collecting, storing, and processing data.',
      children: [
        {
          id: 'stakeholder',
          name: 'Stakeholder',
          description: 'A stakeholder refers to any individual, group, or organization that has an interest or concern in the system, its development, implementation, or outcomes. These stakeholders can be internal or external to the organization implementing the information system. Understanding and managing stakeholders is crucial for the success of an information system project.',
          children: [],
        },
        {
          id: 'infrastructure',
          name: 'Infrastructure',
          description: 'Infrastructure is the foundation or framework that supports a system or organization.',
          children: [],
        }
      ],
    }
  ]);
  const [selectedHolon, setSelectedHolon] = useState(null);

  const selectHolon = (node) => {
    setSelectedHolon(node);
  };

  const addChild = (parentId) => {
    // Simplified example: Prompt for a name and add a child
    const childName = prompt('Enter the name of the new child:');
    if (!childName) return;

    const addChildRecursively = (nodes, parentId, childName) => {
      return nodes.map(node => {
        if (node.id === parentId) {
          const newNode = { id: Date.now().toString(), name: childName, children: [] };
          return { ...node, children: [...node.children, newNode] };
        } else if (node.children.length) {
          return { ...node, children: addChildRecursively(node.children, parentId, childName) };
        }
        return node;
      });
    };

    setNodes(prevNodes => addChildRecursively(prevNodes, parentId, childName));
  };

  const removeChild = (nodeId) => {
    // Simplified example: Remove a child
    const removeNodeRecursively = (nodes, nodeId) => {
      return nodes.reduce((acc, node) => {
        if (node.id !== nodeId) {
          const filteredChildren = removeNodeRecursively(node.children, nodeId);
          return [...acc, { ...node, children: filteredChildren }];
        }
        return acc;
      }, []);
    };

    setNodes(prevNodes => removeNodeRecursively(prevNodes, nodeId));
  };

  return (
    <div className="app-container">
      <div className="left-panel">
        <div className="process-view">Process View</div>
        <div className="canvas-view">
          {nodes.map((node) => (
            <Holon key={node.id} node={node} onSelect={selectHolon} onAdd={addChild} onRemove={removeChild} />
          ))}
        </div>
      </div>
      <div className="right-panel">
        <div className="tools-view">Tools View</div>
        <div className="detail-view">
          {selectedHolon ? (
            <>
              <h2>Holon Details</h2>
              <p>Name: {selectedHolon.name}</p>
              <p>Description: {selectedHolon.description || "No description provided."}</p>
            </>
          ) : (
            <p>Select a holon to see details.</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default App;
