import React from 'react';
import './styles.css'; // Import the CSS file containing styles

// Generic Node Component
const NodeComponent = ({ name, onAdd, onRemove }) => {
  return (
    <div className="node-container">
      <h2>{name}</h2>
      <button onClick={onAdd} className="add-button">+</button>
      <button onClick={onRemove} className="remove-button">x</button>
    </div>
  );
};

// Information System Component (Parent Container)
const InformationSystem = ({ onAddStakeholder, onAddInfrastructure }) => {
  return (
    <div className="information-system-container">
      <h2>Information System</h2>
      <NodeComponent name="Stakeholder" onAdd={onAddStakeholder} />
      <NodeComponent name="Infrastructure" onAdd={onAddInfrastructure} />
    </div>
  );
};

// App Component
const App = () => {
  // State for managing lists
  const [stakeholderList, setStakeholderList] = React.useState([]);
  const [infrastructureList, setInfrastructureList] = React.useState([]);

  // Handlers for adding/removing nodes
  const addNode = (type, name) => {
    if (type === 'stakeholder') {
      setStakeholderList([...stakeholderList, name]);
    } else if (type === 'infrastructure') {
      setInfrastructureList([...infrastructureList, name]);
    }
  };

  const removeNode = (type, index) => {
    let updatedList = type === 'stakeholder' ? [...stakeholderList] : [...infrastructureList];
    updatedList.splice(index, 1);
    if (type === 'stakeholder') {
      setStakeholderList(updatedList);
    } else if (type === 'infrastructure') {
      setInfrastructureList(updatedList);
    }
  };

  return (
    <div className="container">
      <InformationSystem
        onAddStakeholder={() => addNode('stakeholder', 'New Stakeholder')}
        onAddInfrastructure={() => addNode('infrastructure', 'New Infrastructure')}
      />
      <div className="nodes-list">
        {stakeholderList.map((stakeholder, index) => (
          <NodeComponent
            key={index}
            name={stakeholder}
            onRemove={() => removeNode('stakeholder', index)}
          />
        ))}
        {infrastructureList.map((infrastructure, index) => (
          <NodeComponent
            key={index}
            name={infrastructure}
            onRemove={() => removeNode('infrastructure', index)}
          />
        ))}
      </div>
    </div>
  );
};

export default App;
