import React, { useState } from 'react';
import './styles.css'; // Import the CSS file containing styles

const Holon = ({ id, name, onAdd, onRemove }) => {
  const [childName, setChildName] = useState('');

  const handleAddChild = () => {
    if (childName.trim() === '') {
      alert('Please enter a valid name for the child.');
      return;
    }

    onAdd(id, childName);
    setChildName('');
  };

  return (
    <div className="holon">
      <div className="name">{name}</div>
      <button className="add-button" onClick={handleAddChild}>+</button>
      <button className="remove-button" onClick={() => onRemove(id)}>x</button>
    </div>
  );
};

const Connector = ({ from, to }) => {
  const fromNode = document.getElementById(from);
  const toNode = document.getElementById(to);

  if (!fromNode || !toNode) return null;

  const fromRect = fromNode.getBoundingClientRect();
  const toRect = toNode.getBoundingClientRect();

  const fromX = fromRect.left + fromRect.width / 2;
  const fromY = fromRect.top + fromRect.height;

  const toX = toRect.left + toRect.width / 2;
  const toY = toRect.top;

  return (
    <svg className="connector">
      <line x1={fromX} y1={fromY} x2={toX} y2={toY} stroke="#000" strokeWidth="2" />
    </svg>
  );
};

const App = () => {
  const [nodes, setNodes] = useState([
    {
      id: 'stakeholder',
      name: 'Stakeholder',
      children: [],
    },
    {
      id: 'root',
      name: 'Information System',
      children: [],
    },
    {
      id: 'infrastructure',
      name: 'Infrastructure',
      children: [],
    }
  ]);

  const addChild = (parentId, childName) => {
    setNodes(prevNodes => {
      const parentIndex = prevNodes.findIndex(node => node.id === parentId);
      if (parentIndex !== -1) {
        if (prevNodes[parentIndex].children.some(child => child.name === childName)) {
          alert('This node already exists.');
          return prevNodes;
        }
        const newChild = { id: childName.toLowerCase().replace(/\s/g, '-'), name: childName, children: [] };
        return [
          ...prevNodes.slice(0, parentIndex),
          {
            ...prevNodes[parentIndex],
            children: [...prevNodes[parentIndex].children, newChild]
          },
          ...prevNodes.slice(parentIndex + 1)
        ];
      }
      return prevNodes;
    });
  };

  const removeChild = (childId) => {
    setNodes(prevNodes => {
      const index = prevNodes.findIndex(node => node.id === childId);
      if (index !== -1) {
        return [
          ...prevNodes.slice(0, index),
          ...prevNodes.slice(index + 1)
        ];
      }
      return prevNodes;
    });
  };

  return (
    <div className="container">
      <div className="stakeholder-container">
        {nodes.map(node => (
          <React.Fragment key={node.id}>
            <Holon
              id={node.id}
              name={node.name}
              onAdd={(childName) => addChild(node.id, childName)}
              onRemove={removeChild}
            />
            {node.id !== 'root' && <Connector from="root" to={node.id} />}
          </React.Fragment>
        ))}
      </div>
    </div>
  );
};

export default App;
