import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlus, faTrash } from '@fortawesome/free-solid-svg-icons';

const Holon = ({ node, onAddChild, onRemoveNode, onSelectNode, isSelected }) => {
    const handleActionClick = (e, action) => {
        e.stopPropagation();
        action();
    };

    const selected = isSelected(node);

    return (
        <div
            className={`tree-label ${selected ? 'bg-primary text-white' : 'bg-light'}`}
            onClick={() => onSelectNode(node)}
        >
            <div className="d-flex align-items-center justify-content-between">
                <div>{node.name}</div>
                <div>
                    <button
                        className="btn btn-outline-primary btn-sm me-1"
                        onClick={(e) => handleActionClick(e, () => onAddChild(node, true))}
                    >
                        <FontAwesomeIcon icon={faPlus} />
                    </button>
                    <button
                        className="btn btn-outline-danger btn-sm"
                        onClick={(e) => handleActionClick(e, () => onRemoveNode(node.id))}
                    >
                        <FontAwesomeIcon icon={faTrash} />
                    </button>
                </div>
            </div>
        </div>
    );
};

const StructurePage = ({ nodes, onAddChild, onRemoveNode, onSelectNode, isSelected }) => {
    const [showModal, setShowModal] = useState(false);

    const handleClose = () => setShowModal(false);
    const handleShow = () => setShowModal(true);

    return (
        <div className="container mt-4">
            <h1>Structure Page</h1>
            <div className="tree-container mt-4">
                {/* Tree structure */}
            </div>

            <button className="btn btn-primary mt-4" onClick={handleShow}>
                Launch Modal
            </button>

            {/* Bootstrap Modal */}
            <div className={`modal ${showModal ? 'show' : ''}`} tabIndex="-1" role="dialog">
                <div className="modal-dialog modal-lg" role="document">
                    <div className="modal-content">
                        <div className="modal-header">
                            <h5 className="modal-title">Modal Title</h5>
                            <button type="button" className="btn-close" onClick={handleClose}></button>
                        </div>
                        <div className="modal-body">
                            <p>Additional information or interactions can be placed here.</p>
                        </div>
                        <div className="modal-footer">
                            <button type="button" className="btn btn-secondary" onClick={handleClose}>
                                Close
                            </button>
                            <button type="button" className="btn btn-primary" onClick={handleClose}>
                                Save Changes
                            </button>
                        </div>
                    </div>
                </div>
            </div>
            {/* End of Bootstrap Modal */}
        </div>
    );
};

export default StructurePage;
