import React, { useState } from 'react';
import { Tree, TreeNode } from 'react-organizational-chart';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlus, faTrash } from '@fortawesome/free-solid-svg-icons';
import './styles.css';

const Holon = ({ node, onAddChild, onRemoveNode, onSelectNode, isSelected, addNewRoot }) => {
    const [isChildSelected, setIsChildSelected] = useState(false);
    const [isParentSelected, setIsParentSelected] = useState(false);
    const [showModal, setShowModal] = useState(false);
    const [newNodeName, setNewNodeName] = useState('');

    const handleActionClick = (e, action) => {
        e.stopPropagation();
        action();
    };

    const handleRadioChange = (e, type) => {
        if (type === 'child') {
            setIsChildSelected(true);
            setIsParentSelected(false);
        } else {
            setIsParentSelected(true);
            setIsChildSelected(false);
        }
    };

    const handleInputChange = (e) => {
        setNewNodeName(e.target.value);
    };

    const handleAddNode = () => {
        setShowModal(true);
    };

    const handleConfirmAdd = () => {
        if (isChildSelected) {
            onAddChild(node, true, newNodeName);
        } else if (isParentSelected) {
            addNewRoot(newNodeName);
        }
        setNewNodeName('');
        setIsChildSelected(false);
        setIsParentSelected(false);
        setShowModal(false);
    };

    const selected = isSelected(node);

    return (
        <>
            <div className={`tree-label ${selected ? 'selected' : ''}`} onClick={() => onSelectNode(node)}>
                <div className="name">{node.name}</div>
                <div className="buttons">
                    <label>
                        <input
                            type="radio"
                            name={`node_${node.id}`}
                            checked={isChildSelected}
                            onChange={(e) => handleRadioChange(e, 'child')}
                        />
                        Child
                    </label>
                    <label>
                        <input
                            type="radio"
                            name={`node_${node.id}`}
                            checked={isParentSelected}
                            onChange={(e) => handleRadioChange(e, 'parent')}
                        />
                        Parent
                    </label>
                    <button className="add-button" onClick={handleAddNode}>
                        Add
                    </button>
                    <button className="remove-button" onClick={(e) => handleActionClick(e, () => onRemoveNode(node.id))}>
                        <FontAwesomeIcon icon={faTrash} />
                    </button>
                </div>
            </div>
            {showModal && (
                <div className="modal">
                    <div className="modal-content">
                        <span className="close" onClick={() => setShowModal(false)}>&times;</span>
                        <input
                            type="text"
                            value={newNodeName}
                            onChange={handleInputChange}
                            placeholder="Enter node name"
                        />
                        <button onClick={handleConfirmAdd}>Confirm</button>
                    </div>
                </div>
            )}
        </>
    );
};

const StructurePage = ({ nodes, onAddChild, onRemoveNode, onSelectNode, isSelected, addNewRoot }) => {
    return (
        <Tree lineWidth={"2px"} lineColor={"blue"} lineBorderRadius={"5px"}  lineStyle={"dashed"}>
            <Holon
                node={nodes}
                onAddChild={onAddChild}
                onRemoveNode={onRemoveNode}
                onSelectNode={(node) => onSelectNode(node)}
                isSelected={(node) => isSelected && node.id === isSelected.id}
                addNewRoot={addNewRoot}
            />
        </Tree>
    );
};

export default StructurePage;
