import React, { useState, useCallback } from 'react';
import './App.css';

// Initial system data with unique identifiers for each holon
const initialSystem = {
  id: '1',
  name: 'Information System',
  description: 'Top-level entity of an information system.',
  children: [],
};

function App() {
  const [system, setSystem] = useState([initialSystem]);
  const [selectedHolon, setSelectedHolon] = useState(null);

  const addHolon = useCallback((parentId) => {
    const name = prompt('Enter holon name:');
    const description = prompt('Enter holon description:');
    if (!name || !description) return;

    const newHolon = {
      id: Date.now().toString(),
      name,
      description,
      children: [],
    };

    const addHolonToSystem = (holons) => holons.map(holon => {
      if (holon.id === parentId) {
        return { ...holon, children: [...holon.children, newHolon] };
      } else if (holon.children.length) {
        return { ...holon, children: addHolonToSystem(holon.children) };
      }
      return holon;
    });

    setSystem(addHolonToSystem(system));
  }, [system]);

  const removeHolon = useCallback((holonId) => {
    const removeHolonFromSystem = (holons) => holons.reduce((acc, holon) => {
      if (holon.id === holonId) return acc;
      const updatedChildren = removeHolonFromSystem(holon.children);
      return [...acc, { ...holon, children: updatedChildren }];
    }, []);

    setSystem(removeHolonFromSystem(system));
  }, [system]);

  const handleExportSystem = useCallback(() => {
    const filename = "system_export.json";
    const jsonStr = JSON.stringify(system, null, 2);
    const element = document.createElement('a');
    element.setAttribute('href', 'data:text/json;charset=utf-8,' + encodeURIComponent(jsonStr));
    element.setAttribute('download', filename);
    document.body.appendChild(element);
    element.click();
    document.body.removeChild(element);
  }, [system]);

  const renderSystem = (holons) => {
    return holons.map((holon) => (
      <div key={holon.id} className="holon">
        <div onClick={() => setSelectedHolon(holon)}>{holon.name}</div>
        <button onClick={() => addHolon(holon.id)}>Add</button>
        {holon.id !== '1' && <button onClick={() => removeHolon(holon.id)}>Remove</button>}
        {holon.children && <div className="children">{renderSystem(holon.children)}</div>}
      </div>
    ));
  };

  return (
    <div className="app">
      <div className="left-panel">
        <button onClick={handleExportSystem} className="export-btn">Export System</button>
      </div>
      <div className="center-panel">{renderSystem(system)}</div>
      <div className="right-panel">
        {selectedHolon ? (
          <div>
            <h2>Holon Details</h2>
            <p>Name: {selectedHolon.name}</p>
            <p>Description: {selectedHolon.description}</p>
          </div>
        ) : <div>No Selection</div>}
      </div>
    </div>
  );
}

export default App;
