    color: #343a40;
  }

  .cta-text {
    font-family: 'Roboto', sans-serif;
    font-size: 1.2rem;
    color: #0d6efd;
    margin-top: 20px;
    font-weight: bold;
  }

  .form-title {
    font-family: 'Roboto', sans-serif;
    font-weight: bold;
    color: #0d6efd;
  }

  .section-title {
    font-family: 'Roboto', sans-serif;
