import React, { useState, useRef } from 'react';
import { Tree, TreeNode } from 'react-organizational-chart';
import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlus } from '@fortawesome/free-solid-svg-icons';
import './styles.css';

const Holon = ({ node, onSelectNode, isSelected, selectedHolonId, setSelectedHolonId }) => {
    const [isHovered, setIsHovered] = useState(false);
    const [regulationChannelPosition, setRegulationChannelPosition] = useState(null);
    const holonRef = useRef(null);

    const handleNodeClick = () => {
        onSelectNode(node);
    };

    const handleAddClick = (e) => {
        e.stopPropagation();
        if (selectedHolonId === node.id) {
            setSelectedHolonId(null); // Deselect if already selected
        } else {
            setSelectedHolonId(node.id); // Update the selected holon ID
        }
    };

    const handleMouseEnter = () => {
        setIsHovered(true);
    };

    const handleMouseLeave = () => {
        setIsHovered(false);
    };

    const selected = isSelected(node);
    const isOrigin = selectedHolonId === node.id;

    const calculateRegulationChannelPosition = () => {
        const holonRect = holonRef.current.getBoundingClientRect();
        return {
            x1: holonRect.left + holonRect.width / 2,
            y1: holonRect.top + holonRect.height / 2,
        };
    };

    const drawRegulationChannel = (e) => {
        const { x1, y1 } = calculateRegulationChannelPosition();
        setRegulationChannelPosition({ x1, y1, x2: e.clientX, y2: e.clientY });
    };

    const clearRegulationChannel = () => {
        setRegulationChannelPosition(null);
    };

    return (
        <div
            ref={holonRef}
            className={`tree-label ${selected ? 'selected' : ''} ${isOrigin ? 'origin' : ''} ${isHovered ? 'hovered' : ''}`}
            onClick={handleNodeClick}
            onMouseEnter={handleMouseEnter}
            onMouseLeave={handleMouseLeave}
            onMouseMove={isHovered && !isOrigin ? drawRegulationChannel : null}
            onMouseOut={isHovered && !isOrigin ? clearRegulationChannel : null}
        >
            <div className="name">{node.name}</div>
            <div className="buttons">
                <button className="add-button" onClick={handleAddClick}>
                    <FontAwesomeIcon icon={faPlus} />
                </button>
            </div>
            {regulationChannelPosition && (
                <svg className="regulation-channel">
                    <line
                        x1={regulationChannelPosition.x1}
                        y1={regulationChannelPosition.y1}
                        x2={regulationChannelPosition.x2}
                        y2={regulationChannelPosition.y2}
                        stroke="green"
                        strokeWidth="2"
                    />
                </svg>
            )}
        </div>
    );
};

const CommunicationPage = () => {
    const initialNodes = {
        id: 'root',
        name: 'Information System',
        description: 'An information system is an organized combination of people, hardware, software, communication networks, and data resources that collects, transforms, and disseminates information in an organization.',
        children: [
            {
                id: 'Stakeholder',
                name: 'Stakeholder',
                description: 'A stakeholder refers to any individual, group, or organization that has an interest or concern in the system, its development, implementation, or outcomes. These stakeholders can be internal or external to the organization implementing the information system. Understanding and managing stakeholders is crucial for the success of an information system project.',
                children: [],
            },
            {
                id: 'Infrastructure',
                name: 'Infrastructure',
                description: 'Infrastructure in the context of information systems refers to the fundamental physical and organizational structures, facilities, and services required for the operation of an information system. This includes hardware, software, networking components, data centers, and the personnel that maintain and manage the system. Effective infrastructure supports the smooth and efficient collection, storage, processing, and dissemination of information.',
                children: [],
            }
        ],
    };

    const [selectedHolonId, setSelectedHolonId] = useState(null);

    const handleSelectNode = (node) => {
        setSelectedHolonId(node.id);
    };

    return (
        <div className="app-container">
            <div className="content-area">
                <Tree lineWidth={"2px"} lineColor={"blue"} lineBorderRadius={"5px"} lineStyle={"dashed"}>
                    <Holon
                        node={initialNodes}
                        onSelectNode={handleSelectNode}
                        isSelected={(node) => selectedHolonId === node.id}
                        setSelectedHolonId={setSelectedHolonId}
                        selectedHolonId={selectedHolonId}
                    />
                </Tree>
            </div>
        </div>
    );
};

export default CommunicationPage;
