import React, { useState } from 'react';
import { Tree, TreeNode } from 'react-organizational-chart';
import './styles.css';

// Adjusted Holon component for better clarity and functionality
const Holon = ({ node, onAddChild, onRemoveNode, onSelectNode, isSelected }) => {
  const handleActionClick = (e, action) => {
    e.stopPropagation();
    action();
  };

  // Adjust isSelected check to directly compare nodes rather than their IDs
  const selected = isSelected(node);

  return (
    <TreeNode label={
      <div className={`tree-label ${selected ? 'selected' : ''}`} onClick={() => onSelectNode(node)}>
        <div className="name">{node.name}</div>
        <div className="buttons">
          <button className="add-button" onClick={(e) => handleActionClick(e, () => onAddChild(node))}>+</button>
          {node.id !== 'root' && (
            <button className="remove-button" onClick={(e) => handleActionClick(e, () => onRemoveNode(node.id))}>x</button>
          )}
        </div>
      </div>
    }>
      {node.children && node.children.map(child => (
        <Holon 
          key={child.id} 
          node={child} 
          onAddChild={onAddChild} 
          onRemoveNode={onRemoveNode} 
          onSelectNode={onSelectNode} 
          isSelected={isSelected} // Pass down isSelected function for recursive checks
        />
      ))}
    </TreeNode>
  );
};

// App component adjustment for handling selection and descriptions
const App = () => {
  const initialNodes = {
    id: 'root',
    name: 'Information System',
    description: 'An information system is an organized combination of people, hardware, software, communication networks, and data resources that collects, transforms, and disseminates information in an organization.',
    children: [
      {
        id: 'stakeholder',
        name: 'Stakeholder',
        description: 'A stakeholder refers to any individual, group, or organization that has an interest or concern in the system, its development, implementation, or outcomes.',
        children: [],
      },
      // Add more children as needed
    ],
  };

  const [nodes, setNodes] = useState(initialNodes);
  const [selectedHolon, setSelectedHolon] = useState(null);

  // Function to add a child node
  const onAddChild = (parentNode) => {
    const childName = prompt('Enter the name of the child:');
    if (!childName) return;

    const newChild = {
      id: `${parentNode.id}-${Math.random()}`,
      name: childName,
      children: []
    };

    const addNode = (nodes, parentId) => {
      if (nodes.id === parentId) {
        nodes.children.push(newChild);
        return;
      }

      nodes.children.forEach((child) => addNode(child, parentId));
    };

    const newNodes = { ...nodes };
    addNode(newNodes, parentNode.id);
    setNodes(newNodes);
  };

  const onRemoveNode = (nodeId) => {
    const removeNode = (nodes, nodeIdToRemove) => {
      nodes.children = nodes.children.filter((child) => child.id !== nodeIdToRemove);
      nodes.children.forEach((child) => removeNode(child, nodeIdToRemove));
    };

    const newNodes = { ...nodes };
    removeNode(newNodes, nodeId);
    setNodes(newNodes);
    setSelectedHolon(null); // Deselect if selected node is removed
  };

  const isSelected = (node) => selectedHolon && node.id === selectedHolon.id;


  return (
    <div className="app-container">
      <Tree lineWidth={"2px"} lineColor={"blue"} lineBorderRadius={"5px"}>
        <Holon
          node={nodes}
          onAddChild={onAddChild}
          onRemoveNode={onRemoveNode}
          onSelectNode={setSelectedHolon}
          isSelected={isSelected}
        />
      </Tree>
      {selectedHolon && (
        <div className="holon-details">
          <h2>Holon Details</h2>
          <p>Name: {selectedHolon.name}</p>
          <p>Description: {selectedHolon.description || "No description available."}</p>
        </div>
      )}
    </div>
  );
};

export default App;
