import React, { useState } from 'react';
import { Tree, TreeNode } from 'react-organizational-chart';
import './App.css';

const Holon = ({ node, onAdd, onRemove, onSelect }) => {
  return (
    <div className="holon" onClick={() => onSelect(node)}>
      <div className="name">{node.name}</div>
      <div className="buttons">
        <button className="add-button" onClick={(e) => { e.stopPropagation(); onAdd(node); }}>+</button>
        {node.id !== 'root' && (
          <button className="remove-button" onClick={(e) => { e.stopPropagation(); onRemove(node.id); }}>x</button>
        )}
      </div>
    </div>
  );
};


const App = () => {
  const [nodes, setNodes] = useState({
    id: 'root',
    name: 'Information System',
    description: 'An Information System is a set of components for collecting, storing, and processing data.',
    children: [
      {
        id: 'stakeholder',
        name: 'Stakeholder',
        description: 'A stakeholder refers to any individual, group, or organization that has an interest or concern in the system, its development, implementation, or outcomes. These stakeholders can be internal or external to the organization implementing the information system. Understanding and managing stakeholders is crucial for the success of an information system project.',
        children: [],
      },
      {
        id: 'infrastructure',
        name: 'Infrastructure',
        description: 'Infrastructure is the foundation or framework that supports a system or organization.',
        children: [],
      },
    ],
  });
  const [idCounter, setIdCounter] = useState(3);

  const onAdd = (parent) => {
    const childName = prompt('Enter the name of the child:');
    const description = 'New child node';
    if (!childName) return;

    const newNode = {
      id: `node-${idCounter}`,
      name: childName,
      description,
      children: [],
    };

    const addNodeRecursively = (node, parentId) => {
      if (node.id === parentId) {
        node.children = [...node.children, newNode];
        return;
      }
      node.children.forEach((child) => addNodeRecursively(child, parentId));
    };

    const newNodes = { ...nodes };
    addNodeRecursively(newNodes, parent.id);
    setNodes(newNodes);
    setIdCounter(idCounter + 1);
  };

  const onRemove = (nodeId) => {
    const removeNodeRecursively = (node, removeId) => {
      node.children = node.children.filter((child) => child.id !== removeId);
      node.children.forEach((child) => removeNodeRecursively(child, removeId));
    };

    const newNodes = { ...nodes };
    removeNodeRecursively(newNodes, nodeId);
    setNodes(newNodes);
  };

  const renderTreeNodes = (node) => (
    <TreeNode label={<Holon node={node} onAdd={onAdd} onRemove={onRemove} />} key={node.id}>
      {node.children.map((child) => renderTreeNodes(child))}
    </TreeNode>
  );

  return (
    <div className="app-container">
      <Tree lineWidth={"2px"} lineColor={"blue"} lineBorderRadius={"5px"}>
        {renderTreeNodes(nodes)}
      </Tree>
    </div>
  );
};

export default App;
